INSERT INTO routes VALUES(1,'Hot August Nights Ice Cream Run','2002-08-17','Bob & Genny Paauwe',replace(replace('Care to follow us down a rocky road?  This fun run is sherbet to be\r\nsatisfying!  Everyone likes a frozen treat on a hot August night, so\r\njoin us for this evening event which will end at the Tasty Time ice cream parlor in Folsom. The run should be under 2 hours in length.\r\n<p>\r\nMeet at the Cameron Park McDonald’s where we’ll shake it up on the back roads of El Dorado County.  Don’t be (choco)late!','\r',char(13)),'\n',char(10)),replace(replace('0.00|Out of the parking lot and RIGHT onto Durock Rd.\r\nRIGHT onto South Shingle\r\n10.25|LEFT onto Latrobe Rd.\r\nLEFT on Old Sacramento Rd.\r\nLEFT on Hwy 49 (in Plymouth)\r\n29.60|RIGHT onto Union Mine Rd. (Watch for deer!)\r\nLEFT at T (stay on Union Mine Rd.)\r\nRIGHT onto Hwy 49\r\nRIGHT onto Pleasant Valley Rd. / Hwy 49\r\nLEFT onto Forni Rd (comes up quickly)\r\nSTRAIGHT at Missouri Flat Rd. (Signal)\r\nRIGHT at Placerville Dr. (staying on Forni Rd.)\r\nLEFT onto Ray Lawyer Dr.\r\n42.45|STRAIGHT at SIGNAL onto Green Valley Rd.\r\nRIGHT at STOP, stay on Green Valley Rd.\r\nBEAR LEFT at Y.\r\nSTRAIGHT at STOP (staying on Green Valley Rd.)\r\nRIGHT at Green Valley Rd.\r\n57.50|RIGHT onto Deer Valley Rd.\r\nRIGHT onto Green Valley Rd.\r\nSTRAIGHT at SIGNAL (Salmon Falls Rd)\r\nSTRAIGHT at SIGNAL (Francisco Dr.)\r\nSTRAIGHT at SIGNAL (Mormon Island)\r\nSTRAIGHT at SIGNAL (Sophia)\r\nSTRAIGHT at SIGNAL (E. Natomas St.)\r\n63.40|RIGHT into Parking lot\r\n-|Tasty Time faces E. Natomas, to the right of Ralphs, around the corner. There should be plenty of parking off to the right but probably not enough directly in front of the shop.','\r',char(13)),'\n',char(10)),replace(replace('Eat ice cream at Tasty Time!\r\n<p>\r\nFinish is at Tasty Time<br>\r\n25004 Blue Ravine Rd # 109<br>\r\n(916) 353-0441<br>\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Meet at the Cameron Park McDonald’s (6:30 p.m.), Hwy. 50 at Cameron Park\r\nexit, south of the freeway, across from Sam’s Town.  The fun run will\r\nstart promptly at 6:45 p.m., where we’ll shake it up on the back roads\r\nof El Dorado County.  Don’t be (choco)late!','\r',char(13)),'\n',char(10)),'',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(2,'Power House Run  and Tour','2002-03-03','Genny & Bob Paauwe and Staci Overma','The start is at the McDonald''s in El Dorado Hills. South of Hwy. 50 on Latrobe off the El Dorado Hills Blvd/Latrobe Rd. exit. The total distance is approximately 65 miles and it should take about 2 hours, 30 minutes. End is a tour at the Folsom Historic Power House, Folsom.',replace(replace('RIGHT out of parking lot \r\n0.0|RIGHT on Post St.\r\nRIGHT on White Rock Rd.\r\nLEFT at SIGNAL onto Latrobe Rd. (Regroup past a signal or two)\r\nSTRAIGHT at STOP\r\nLEFT onto Jackson Hwy.\r\nRIGHT on Carbondale Rd. (occurs quickly, regroup)\r\nLEFT on Irish Hill Rd.\r\nLEFT at T\r\nRIGHT on Five Mile Dr.\r\nLEFT on West Marlette St.\r\nRIGHT at T\r\nRIGHT on South Church St.\r\n29.7|LEFT into Charles Howard Park (Potty Break)\r\n0.0|RIGHT out of park onto South Church St.\r\nLEFT onto Hwy 104\r\nRIGHT staying on Hwy 104\r\nRIGHT at Y (Ione Michigan Bar Rd.)\r\nRIGHT on Carbondale Rd.\r\nLEFT on Maxwell (paved, many times, bumpy)\r\nRIGHT at STOP (Ione Rd.)\r\n14.5|LEFT on Jackson Hwy\r\nSTRAIGHT at next 2 SIGNALS\r\n18.0|RIGHT on Stone House Rd. (regroup)\r\nRIGHT on Scott Rd. (Watch for pot holes)\r\nLEFT on White Rock Rd.\r\nRIGHT on Prairie City Rd.\r\nSTRAIGHT at next 6 SIGNALS\r\n31.8|LEFT at STOP (Glenn Dr., regroup)\r\nRIGHT at SIGNAL (Folsom Blvd.)\r\nSTRAIGHT at SIGNAL\r\nRIGHT at SIGNAL (Greenback Ln.)\r\nCross the Rainbow Bridge\r\n34.4|RIGHT into Parking area\r\n','\r',char(13)),'\n',char(10)),'Finish at the Folsom Historic Power House for a guided tour. The tour starts at 3:00pm and will last about 1 hour.','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(3,'Drive & Skate','2002-01-13','Bob and Genny Paauwe',replace(replace('Start: Denny''s in Newcastle at the intersection of I-80 and Newcastle Rd., south of the freeway.  Ends with ice skating at Roseville Skatetown.\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Right out of parking lot onto Newcastle Rd.\r\nStraight at SIGNAL\r\nRight onto Old State Hwy.\r\nRight at STOP\r\nRight at STOP (Taylor Rd./Hwy 193)\r\nLeft at Ridge Rd.\r\nRight at STOP/YIELD onto Gold Hill Rd.\r\nRight onto Chili Hill Rd.\r\nRight at Y (Continue on Chili Hill Rd.)\r\nLeft onto Lozanos Rd.\r\nLeft onto Wise Rd.\r\nStraight at STOP \r\nBear Left to stay on Wise Rd.\r\nRight at STOP (stay on Wise Rd.)\r\nRight at STOP (Mt Vernon)\r\nRight on Mt. Vernon\r\nRight onto Bald Hill Rd.\r\nLeft onto Bald Hill Rd.\r\nStraight at STOP\r\nLeft onto Lozanos Rd.\r\nRight at YIELD onto Ophir Rd.\r\nRight at STOP (Taylor Rd./Hwy 193)\r\nLeft at STOP\r\nLeft onto Old State Hwy. (Go through tunnel)\r\nLeft at STOP (Newcastle Rd. - go over freeway)\r\nFollow Newcastle Rd.\r\nRight onto Brennans Rd.\r\nStay left on Brennans at Rock Spings.\r\nRight onto King Rd.\r\nLeft onto Val Verde Rd. (turns into Wells Ave.)\r\nLeft at STOP (Laird Rd.)\r\nRight at STOP (Cavitt Stallman Rd.)\r\nStraight at STOP\r\nLeft at STOP (Sierra College Blvd.)\r\nREGROUP ON SHOULDER\r\nStraight at SIGNAL\r\nRight onto Old Auburn Blvd. (SIGNAL)\r\nRight at SIGNAL (S. Cirby Wy.)\r\nStraight at next 6 SIGNALS\r\nLeft at SIGNAL (Orlando Way)\r\nLeft into SkateTown parking lot. Lots of parking in back','\r',char(13)),'\n',char(10)),replace(replace('Finish is at the Roseville Skate Town ice rink 1009 Olrando Ave. Roseville, CA\r\nThe phone number there is: (916) 783-8550.\r\n<p>\r\nThe public skating session is from 1:00PM to 5:00PM.\r\n','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(4,'Apple Hill Rally','2000-09-24','Bob & Genny',replace(replace('This is only one of many route instructions for this event. Cars are started at 1 minute intervals and follow a fixed route to the Apple Hill area. Once there, each car has its own unique route to a few different apple growers. \r\n<p>\r\nStart is at the McDonald''s in Cameron Park. From Hyw 50, exit Cameron Park Drive. South on Cameron Park Drive, straight at signal, Right into Safeway shopping center.<br>\r\n<p>\r\nEnd is lunch Bavarian Hills in Apple Hill (Camino).','\r',char(13)),'\n',char(10)),replace(replace('0.00|Right out of parking lot onto Durock Rd. at     9:01 AM \r\n2.22|Left at STOP (South Shingle) \r\n2.25|Right onto Mother Lode (occurs quickly) \r\n6.25|Right at Y (Pleasant Valley, towards Diamond Springs) \r\n7.53|Straight at STOP \r\n7.98|Straight at Signal \r\n8.60|Straight at STOP \r\n9.14|Straight at Missouri Flat \r\n9.78|Straight at STOP \r\n11.23|Left on Big Cut Rd. (sign on left) \r\n11.58|Right on Quarry \r\n13.25|Right on Cedar Ravine \r\n14.66|Left on Pleasant Valley  \r\nWatch for CHP \r\n18.70|Left on Newtown Rd. (towards Camino) \r\n19.61|Right on Snows Rd. \r\n22.56|Cross under Freeway \r\n22.81|At STOP, Carson Rd. Begin Apple Hill Tour. \r\nYou will visit four apple growers along the way. Finish is at grower #11 (Bavarian Hills Orchard). Use the supplied Apple Hill Scenic Drive Map to find your way each of the growers. Remember, there are other tourist here so be careful.   \r\n\r\nVisit 1 Stop in at grower #24 (Honey Bear Ranch) and pick up a Golden Delicious apple \r\nVisit 2 Stop in at grower #45 (Plubell''s Family Orchard) and pick up a Red Delicious apple \r\nVisit 3 Stop in at grower #39 (Goldbud Farms) and pick up a Golden Delicious apple \r\nVisit 4 Stop in at grower #10 (Rainbow Orchards) and pick up a Red Delicious apple','\r',char(13)),'\n',char(10)),replace(replace('Finish is at Bavarian Hills Orchard on North Canyon Rd. The phone number there is: (530) 642-2714. Please arrive at the finish between 12:00pm and 12:30pm so you have time for lunch and can participate in the Apple Olympics. \r\n<p>\r\nIf you have time and want to check out some of the other growers, here''s some we particularly liked: \r\n<p>\r\n#1 High Hill Ranch, they have a trout pond<br> \r\n#3 Boa Vista Orchards, a little of everything<br> \r\n#19 El Dorado Orchards, take a train ride<br> \r\n#36 Jack Russell Farm Brewery, learn how beer is made<br>','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(5,'Memorial Day Picnic Run','2001-05-26','Bob & Genny',replace(replace('A quick run around the foothills ending at a small park in Garden Valley.\r\n<p>\r\nThe start is at the McDonald''s in El Dorado Hills. South of Hwy. 50 on El Dorado Hills Blvd. (exit). ','\r',char(13)),'\n',char(10)),replace(replace('Straight out of parking lot.  \r\nLeft at STOP.  \r\nLeft at STOP. \r\n00.0| Right at STOP onto Latrobe Rd. \r\n5.Straight through many signals. (Becomes El Dorado Hills Blvd. under freeway) \r\n04.0| Straight at STOP. \r\n04.6| Straight at SIGNAL (becomes Salmon Falls Rd.).  \r\n8.Continue on Salmon Falls at Pilot Hill. \r\n16.6| Right into mini mart parking lot to regroup.  \r\n10.Left out of parking lot onto Hwy. 49. \r\n20.0| Right at STOP onto Hwy 193. \r\n26.2| Left onto Sliger Mine Rd. \r\n28.5| Right onto Spanish Diggins Rd.  \r\n14.Keep left at fork. \r\n32.0| Left at STOP onto Reservoir Rd. \r\n32.2| Left at STOP onto Hwy. 193. \r\n32.8| Right at STOP onto Main St. \r\n32.9| Left onto Marshall Rd. \r\n36.4| Left on Black Oak Mine Rd. \r\n37.6| Right on Hwy 193 (STOP). \r\n41.0| Right onto Garden Valley Rd. (Sharp!) \r\n44.6| Finish at Garden Valley Park ','\r',char(13)),'\n',char(10)),replace(replace('Finish is at the Garden Valley Park. There is some parking off Garden Valley Rd., on the right as we arrive. Additional parking is located across the park if you continue to the right. \r\n<p> \r\nMileages are approximates. Based on a 1992 Miata with 15inch stock ''99 wheels.  ','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(6,'Hot August Nights Ice Cream Run 2001','2001-08-18','Bob & Genny',replace(replace('An evening run on a warm August night followed by ice cream.\r\n<p>\r\nThe start is at the McDonald''s in Cameron Park. South of Hwy. 50 on Coach Ln. Off Cameron Park Dr. (exit).\r\n<p>\r\nThe total distance is approximately 53 miles and it should take about 1 hour 20 minutes. ','\r',char(13)),'\n',char(10)),replace(replace('Leave parking lot, Right on Durock Rd.  \r\nLeft at STOP. (South Shingle)  \r\nStraight at STOP.  \r\nRight at STOP onto North Shingle. (Regroup)  \r\nRight at Y (Green Valley)  \r\nRight onto El Dorado Rd.  \r\nStraight at STOP (MotherLode).  \r\nLeft at STOP (Pleasant Valley Rd.) (Regroup)  \r\nRight at STOP (Hwy. 49)  \r\nRight onto Main St. (in Plymouth).  \r\nStraight at STOP.  \r\nRight at STOP (Latrobe Rd.).  \r\nRight onto South Shingle Rd.  \r\nLeft onto Durock Rd.  \r\nStraight at Signal.  \r\nStraight at Signal.  \r\nStraight at Signal.  \r\nRight into the Golderado shopping center, just before the KFC.  \r\nFinish at Buff''s Cruise-N-Classic Yogurt','\r',char(13)),'\n',char(10)),replace(replace('Finish is at Buff''s Cruise-N-Classic Ice Cream and Yogurt shop. 3490 Suite 3H Palmer Dr., Cameron Park   \r\n<p>\r\n<b>Watch for deer as it gets dark</b><br>\r\n<b>Flash headlights if you need to stop</b><br>','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(7,'Bob & Genny''s Wedding Run','2001-10-20','Bob & Genny',replace(replace('The start is at the Park & Ride in El Dorado Hills. South of Hwy. 50 on Latrobe Rd. Off El Dorado Hills Blvd. exit. Across from the Post Office.\r\n<p>\r\nThis is a short run (1 1/2 to 2 hours) to celebrate our marriage. We want to show you some of the roads and places where we got to know each other.\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Leave parking lot, RIGHT on Post St.         \r\nRIGHT at STOP. (White Rock Rd.)  [<i>Bob''s first SAOMA run was Genny''s Hide & Seek, which started here.</i>]      \r\nLEFT at SIGNAL (Latrobe Rd.).         \r\nLEFT onto South Shingle.  [<i>Hmm, Seems like we''ve spent a lot of time out here.</i>]      \r\nRIGHT at STOP (MotherLode, before Freeway)         \r\nRIGHT at Y onto Pleasant Valley Rd.  [<i>Our first run, Apple Hill Olympics, used these roads.</i>]      \r\nLEFT on Newtown Rd. (toward Camino).         \r\nRIGHT on Snows Rd.         \r\nLEFT at STOP (Carson Rd.)  [<i>We spent a lot of time in Apple Hill planning the Apple Hill Olympics</i>]      \r\nPause and REGROUP at Boa Vista Orchards.  [<i>We always stopped here for apple cookies (and sometimes fritters)</i>]      \r\nLeave Boa Vista and continue on Carson Rd. toward Placerville.         \r\nLEFT at Schnell School Rd.         \r\nRIGHT entering Hwy 50 WEST (yes, get on the freeway).  [<i>We really tried to find a way through Placerville without getting on 50, but it''s not easy!</i>]      \r\nRIGHT at SIGNAL (Spring St./Hwy 49).  [<i>OK, we could have gone through town on the south side of 50.</i>]      \r\nTurn LEFT to stay on Hwy 49.          \r\nREGROUP somewhere after/around Hwy 193.  [<i>We''ll be on Hwy 49 for the rest of the trip, enjoy the drive!</i>]      \r\nContinue through Coloma.         \r\nRIGHT into Venezio''s Vineyard.','\r',char(13)),'\n',char(10)),'We are ending at Venezio''s Vineyard (530) 885-WINE. This is where we were married. We''ll have a small reception with cake on the porch of the wine tasting room. If you want to try some of the wine, the tasting room will be open. ','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(8,'Baddtaz Run','2000-07-29','Dennis Detloff & Barbara Fenner','Start: Cameron Park McDonald''s, Hwy 50 at Cameron Park exit, south of the freeway.  Ends with lunch at Tortilla Flats Restaurant in downtown Placerville.',replace(replace('Start at McDonalds on Coach Lane in Cameron Park. Follow Baddtaz out of parking lot and turn Right on Cameron Park Drive. Cameron Park drive becomes Durock Road.\r\nFollow Durock Road to South Shingle and turn left.\r\nFollow South Shingle to Mother Lode and turn right.\r\nTake Mother Lode to Pleasant Valley and turn right.\r\nPleasant Valley becomes Sly Park Road at the Bucks Bar turnoff. Stay on Sly Park road to Pony Express Trail in Pollock Pines. Sly Park Road goes under the freeway and ends at Pony Express Trail.\r\nOur first rest stop is at the 76 Station on Pony Express Trail in Pollock Pines. (This is approximately 3O miles from the beginning)\r\nAfter the rest stop we will get back on Sly Park Road, under the freeway and turn Left onto Hwy 5O.\r\nFollow Hwy 5O to the 76 Station in Silver Fork. Here we will have a short rest stop.\r\nBack onto Hwy 5O and turn right onto Silver Fork Road. (approx. 1 mile)\r\nFollow Silver Fork to Mormon Emigrant Trail and turn right. We have traveled approx. 63 miles by now.)\r\nDrive down Mormon Emigrant Trail until it stops at Sly Park Road (get the feeling of de ja vue?) and turn right and follow Sly Park Road back to the 76 Station in Pollock Pines to regroup for the last leg of this run.\r\nNow follow Pony Express Trail through Pollock Pines to Carson Road.\r\nTurn right onto Carson road and follow until you get to Snows Road\r\nTurn left on Snows road and have a fun ride to Newtown Road.\r\nTurn right on Newtown Road and it is almost over. Follow Newtown Road into Placerville where it becomes Broadway.\r\nDrive down Broadway to the End and this is Main Street. (You should see, you guessed it, a 76 Sign) and turn left onto Main Street.\r\nFollow Main Street until you pass the coffee shop and Sweetie Pies and turn left into the Tortilla Flats Parking lot. You can park on either side of the Restaurant or on the street.\r\n','\r',char(13)),'\n',char(10)),replace(replace('WE MADE IT!. HAVE A GREAT LUNCH. FEEL FREE TO WANDER THE STREETS OF\r\nPLACERVILLE AND ACT LIKE A TOURIST.\r\n','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(9,'Folsom Dam Drive & Tour','2000-08-05','Genny Paauwe','An easy drive around Folsom lake ending with a tour of the Folsom Dam.  Starts at McDonald''s on Auburn-Folsom Rd. in Folsom.  Lunch at Cabos Restaurant in Granite Bay after the tour is optional.',replace(replace('From the parking lot, exit and turn left onto Auburn-Folsom Rd.\r\nContinue down Auburn-Folsom Rd.\r\nAfter Douglas Blvd. light we will regroup.\r\nFollow Auburn-Folsom Rd. into Auburn.\r\nPOTTY STOP at Maidu Market, corner of Auburn-Folsom Rd. and Sacramento St. (on your right); 20 minute break.\r\nFrom the Maidu Market, turn left onto Sacramento St.\r\nAt the third stop sign, turn right onto Pacific Ave.\r\nTurn right onto High St.\r\nTurn right onto Finley St.\r\nTurn right onto the short bridge with the white fence -- there''s no street sign here so be ready when you see the white fencing!\r\nTake an immediate left after you cross the little bridge onto Borland Ave.\r\nAt the traffic lights, turn right onto Hwy. 49 toward Placerville. (Note: there will not be a regroup area until we reach Cool.)\r\nGo down canyon (Hwy. 49) and turn right toward Cool/Placerville (go over bridge).\r\nStay on Hwy. 49 -- we will regroup after we''ve climbed the other side of the canyon and get to Cool.\r\nContinue on Hwy. 49, then turn right onto Rattlesnake Bar Rd.\r\nImmediately turn left onto Salmon Falls Rd., where we will regroup again, if necessary.\r\nContinue to follow Salmon F. for about 20 minutes\r\nTurn right onto Green Valley Rd. at traffic signals.\r\nTurn right onto East Natomas.\r\nTurn right onto Folsom Dam Rd.\r\nCross Folsom Dam and turn left into the Water Education Center. (If you come to traffice lights, you''ve gone too far!)\r\nFollow the blue line to the parking area.','\r',char(13)),'\n',char(10)),replace(replace('Directions to Cabos Restaurant from Folsom Dam:<p>\r\n1. From the tour, exit the way you came in by following the blue line.<br>\r\n2. Make a left onto Folsom Dam Rd.<br>\r\n3. At the traffice signal, make a right onto Auburn-Folsom Rd.<br>\r\n4. Cabos is on the left just before you reach Douglas Blvd. in Granite Bay.\r\n<p>\r\nThe Resaurant looks sort of like an old Spanish mission. If you get lost, you may call them at (916) 797-1996','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(10,'Run for the Gold','2001-06-24','Bob & Peg Campbell',replace(replace('Start: El Dorado Hills McDonald''s, south of Hwy. 50 at Latrobe Rd.\r\nDestination: Ironstone Winery in Murphys, followed by dinner at the Pizza Factory near Placerville.','\r',char(13)),'\n',char(10)),replace(replace('14.0|Exit McDonald''s, turn LEFT onto Latrobe Road, proceed\r\n1.5|Turn RIGHT Old Sacramento Road\r\n1.0|Turn RIGHT Jackson Hwy (Rte. 16)\r\n1.0|Turn LEFT Forest Home Road\r\n0.5|Turn LEFT Carbondale Road\r\n5.4|RIGHT Irish Hill Road\r\n2.5|LEFT Hwy 104\r\n1.0|LEFT W. Main Street\r\n1.4|RIGHT S. Church Street (Hwy 124)\r\n4.7|LEFT Ione/Buena Vista Road\r\n0.8|LEFT Stoney Creek Road\r\nRIGHT into Pardee Reservoir Rec. Area (POTTY BREAK!)\r\n0.1|Exit Pardee Reservoir Rec. Area, turn LEFT onto Stoney Creek Road\r\n1.2|LEFT Pardee Dam Road\r\nLEFT Observation Point\r\n2.7|LEFT Pardee Dam Road\r\n0.1|RIGHT at "T"\r\n2.6|LEFT Water Town Road\r\n5.6|LEFT Laurel St. across Hwy 12 to Hwy 26\r\n7.0|LEFT Jenny Lind Road (Milton Road)\r\n14.1|LEFT Hunt Road\r\n6.8|LEFT Hwy 4\r\n0.2|RIGHT Hwy 49\r\n6.6|LEFT Murphys Grade Road\r\nArrive Murphys, turn RIGHT at Muphys Hotel to get to Ironstone.  2.5 hours of free time.  Regroup at Ironstone parking lot at appointed time for departure.\r\n6.1|RIGHT off Main St., Murphys onto Sheep Ranch Rd.\r\n2.2|LEFT at Avery Rd. junction still Sheep Ranch Rd.\r\n4.5|HARD RIGHT to stay on Sheep Ranch Rd.\r\n8.4|RIGHT at "Y", Railroad Flat Rd.\r\n3.8|RIGHT toward Railroad Flat (still on Railroad Flat Rd.)\r\n1.7|Bear LEFT at stop sign - still Railroad Flat Rd.\r\n0.6|RIGHT Hwy 26 toward West Point\r\n5.2|LEFT Hwy 26\r\n0.9|RIGHT Defender Grade to Pioneer\r\n2.3|LEFT Hwy 88\r\n2.1|RIGHT Pioneer/Volcano Road\r\n0.4|Bear RIGHT Pine Grove/Volcano Road\r\nPOTTY BREAK in Volcano\r\n5.9|Take Consolation out of Volcano towards Daffodil Hill\r\n10.0|LEFT Fiddletown Road\r\n0.06|RIGHT Tyler Road\r\n4.8|RIGHT Tyler Road\r\n2.5|LEFT Bridgeport School Road\r\n1.2|LEFT Cedar Creek Road\r\n0.4|RIGHT Mt. Aukum Road\r\n0.5|RIGHT Omo Ranch Road\r\n2.9|LEFT Dorado Canyon Road\r\n0.6|LEFT Fairplay Road\r\n6.1|RIGHT Mt. Aukum Road\r\nLEFT into shopping center at intersection of Mt. Aukum and Pleasant Valley Roads\r\nDINNER -- Pizza Factory\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Questions for Run for Gold\r\n\r\n<p>1. Where would fishes live? (Watertown Road)<br>\r\n2. What color Miatas rule? (Red)<br>\r\n3. What does Kitty Carson make? (dolls)<br>\r\n4. What town has a good view? (Ione - Buena Vista View Rd.)<br>\r\n5. Who has huge footwear savings? (sorry - no answer!)<br>\r\n6. Where would Don Ho kennel his dog? (Aloha dogs and cats)<br>\r\n7. In what fancy home would Broadway''s original Music Man live? (Preston Castle)<br>\r\n8. Where would Tom go to get away from Jerry? (Tom Cat Ranch)<br>\r\n9. Where would the dragon slayer stay? (St. George Hotel)<br>\r\n10. What famous golfer has a realty office in the foothills? (Sam Snead)<br>\r\n11. Where would you go to look at the stars? (Observation Point)<br>\r\n12. What road would Magic Puff live on? (Happy Valley)<br>','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(11,'American River Run','2001-07-14','Tom & Denise Klisz','Start is at McDonald''s, corner of White Rock Rd. and Sunrise Blvd. (near Costco).  Picnic at Coloma State Park at the end.  Entrance Fee: $2 per car',replace(replace('0.0|RIGHT onto White Rock Rd. from McDonald''s\r\n4.8|Continue LEFT onto White Rock/Grant Line Rd.\r\n12.5|RIGHT onto Latrobe Rd.\r\n20.4|LEFT onto South Shingle Rd.\r\n22.6|RIGHT onto Brandon Rd.\r\n24.7|LEFT onto French Creek Rd.\r\n29.2|RIGHT onto Old French Town Rd.\r\n33.0|RIGHT onto Mother Lode\r\nImmediate LEFT onto Greenstone Rd.\r\n35.5|RIGHT onto Green Valley Rd.\r\n38.9|Continue RIGHT onto Missouri Flat Rd.\r\nREST STOP: LEFT into Jack-in-the-Box for break\r\n(NOTE: Restrooms also next door at Chevron)\r\n39.0|RIGHT back onto Missouri Flat Rd.\r\n42.4|Continue RIGHT onto Green Valley Rd.\r\n45.1|RIGHT onto Lotus Rd.\r\n47.8|RIGHT onto Thompson Hill Rd.\r\n50.8|RIGHT onto Cold Springs Rd.\r\n52.2|RIGHT onto Gold Hill Rd.\r\n54.9|RIGHT onto Lotus Rd.\r\n57.9|RIGHT onto Coloma (Hwy 49)\r\n59.0|LEFT into Entrance of Marshall Gold Discovery State Park, North Beach River Access\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Suggested Return Directions:\r\n<p>\r\n<b>Easiest route to Hwy 80 (and fastest route):<br></b>\r\n<p>\r\nRight on Coloma, Route 49, will take you through Auburn to Hwy 80<br>\r\n<p>\r\n<b>Route to Hwy 50<br></b>\r\n<p>\r\nLeft onto Colome, Route 49, will take you through Placerville<br>\r\n<p>\r\n<b>Fastest Route to Hwy 50<br></b>\r\n<p>\r\nRight on Coloma, Route 49<br>\r\nLeft on Lotus Rd.<br>\r\nLeft on North Shingle (if you go to Folsom, right on Green Valley Rd.),<br>','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(12,'Timbuctoo?','2001-08-12','Matt Balvin','Start: Denny''s in Newcastle (I-80 at Newcastle exit, south side of freeway).  Ends with picnic at the Narrows Recreation site, Lake Englebright.',replace(replace('0.2|RIGHT Newcastle Rd.\r\n0.2|RIGHT Old Lincoln Hwy\r\n0.1|RIGHT Hwy 193\r\n0.3|RIGHT Hwy 193\r\n0.2|LEFT Ophir Rd.\r\n1.0|LEFT Lozanos Rd.\r\n0.4|RIGHT Bald Hill Rd.\r\n4.7|LEFT Wise Rd.\r\n1.1|RIGHT Wise Rd.\r\n0.7|LEFT Wise Rd.\r\n4.7|LEFT Wise Rd.\r\n6.9|RIGHT McCourtney Rd.\r\n5.0|LEFT Camp Far West Rd.\r\n0.05|RIGHT Rioso Rd.\r\n3.9|RIGHT Hwy 65\r\n9.6|PIT STOP - Chevron, Hwy 65/70\r\n0.2|RIGHT Erle Rd. off ramp\r\n1.6|RIGHT, East on Erle Rd.\r\n1.6|LEFT Griffith Rd.\r\n14.2|RIGHT Hammond-Smartville\r\n3.9|RIGHT Hwy 20\r\n0.7|RIGHT Indian Springs\r\n1.9|RIGHT Indian Springs\r\n0.1|LEFT Spencerville Rd.\r\n0.1|PIT STOP - Shell Station, Spencerville Rd.\r\n5.2|RIGHT Hwy 20\r\n10.3|RIGHT McCourtney Rd.\r\n2.3|STRAIGHT Lime Kiln Rd.\r\n1.9|RIGHT Duggans Rd.\r\n1.8|LEFT Wolf Rd.\r\nSTRAIGHT across Hwy 49\r\n1.0|STRAIGHT Combie Rd.\r\n6.6|STRAIGHT Magnolia Rd.\r\n7.3|LEFT Dog Bar Rd.\r\n4.1|RIGHT Rattlesnake Rd.\r\n1.2|RIGHT Hwy 174\r\n2.0|LEFT Brunswich Rd.\r\n3.0|RIGHT Idaho-Maryland Rd.\r\n3.4|LEFT Banner Lava Cap Rd.\r\n0.1|RIGHT Nevada City Hwy\r\n0.1|RIGHT Gold Flat\r\n1.4|LEFT North, Hwy 49/20\r\n8.5|LEFT Hwy 49\r\n14.5|LEFT Pleasant Valley Rd.\r\n4.5|RIGHT Hwy 49\r\n2.2|RIGHT Mooney Flat Rd.\r\nLEFT Narrows Recreation Site\r\n\r\n','\r',char(13)),'\n',char(10)),'Parking is limited at the picnic site near the lake.  For large groups, parking at the top and walking down the road is recommended.','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(13,'Gold Rush Run to Empire Mine','2001-09-22','Staci Overman','Start: McDonald''s on Auburn-Folsom Rd., Folsom (near Folsom bridge).  End is at Empire Mine State Park for a picnic and tour.  Parking is free. Tour Entrance Fee: $2.  ',replace(replace('0.0| Auburn-Folsom Rd. McDonald''s\r\n17.2|North on Auburn-Folsom Rd. to Auburn; Auburn North on Hwy 49\r\n21.7|RIGHT on Dry Creek Rd.(past the airport)\r\n25.6|LEFT on Christian Valley Rd.\r\n26.6|RIGHT on Bancroft Rd.\r\n26.9| BREAK\r\nContinue on Bancroft Rd.\r\n27.8|LEFT on Pinewood Way\r\n28.0|LEFT on Placer Hills Rd.\r\n34.7|LEFT on Dog Bar Rd.\r\n36.3|(continue straight at intersection with Magnolia)\r\n43.7|RIGHT on Rattlesnake Rd.\r\n47.9|LEFT on Hwy 174\r\n48.7|LEFT into Empire Mine State Park','\r',char(13)),'\n',char(10)),'From Empire Mine, take a left back onto Hwy 174 to go to Grass Valley or to get to Hwy 49 south to Auburn or North to Nevada City.','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(14,'Run Around the Hills with Baddtaz','2001-07-28','Dennis Detloff & Barbara Fenner','Start: Cameron Park McDonald''s, Hwy 50 at Cameron Park exit, south of the freeway.  End is at Tortilla Flats restaurant in downtown Placerville.',replace(replace('Leave McDonalds parking lot\r\nTurn RIGHT onto Durock Rd.\r\nLEFT on South Shingle (Turns into Ponderosa)\r\nLEFT at stop onto Green Valley Rd. Travel through Rescue\r\nRIGHT on Salmon Falls Rd.\r\nRIGHT on Salmon Falls Cutoff with quick RIGHT onto Hwy 49\r\nREST STOP - in Coloma for Approx. 15 min.\r\nBack onto Hwy 49 through Coloma and STRAIGHT onto Cold Springs Rd.\r\nRIGHT on Pierroz Rd.\r\nRIGHT on Placerville Dr.\r\nRIGHT onto Forni Rd.\r\nLEFT on Missouri Flat Rd.\r\nLEFT on Pleasant Valley Rd.\r\nRIGHT on Bucks Bar Rd.\r\nLEFT onto Mt. Aukum Rd. when you get into Somerset\r\nRIGHT on Pleasant Valley Rd. to Sly Park (Pleasant Valley becomes Sly Park)\r\nRIGHT on Pont Express Trail to the Crystal View 76 Station\r\nREST STOP - Pollock Pines approx. 15 min.\r\nOut of parking lot, turn RIGHT onto Pony Express Trail\r\nRIGHT onto Carson Rd. (Pony Express Trail becomes Carson Rd.)\r\nLEFT on Snows Rd.\r\nRIGHT onto Newtown Rd. (We will regroup when we turn onto Newtown Rd.)\r\nNewtown becomes Broadway in Placerville.  Follow Broadway (Dead-end); LEFT onto Main St.\r\nLEFT on Cedar Ravine\r\nRIGHT on Pacific St.\r\nRIGHT on Sacramento St.\r\nRIGHT on Main St to the Tortilla Flats parking lot.','\r',char(13)),'\n',char(10)),'The restaurant is located on the right side of the road.  Parking is available on both sides of the Tortilla Flats Restaurant.','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(15,'Indian Grinding Rock State Park','2000-06-10','Robin & Jeanne George','Start: McDonald''s at White Rock Rd. and Sunrise Blvd. (near Costco).  End is a picnic at Indian Grinding Rock State Park in Amador County.  Entrance fee: $2',replace(replace('L at stop sign, White Rock Rd.\r\nR Latrobe Rd. (regroup if necessary)\r\nL Old Sacramento Rd. (WARNING WATCH FOR WILD TURKEYS)\r\nL at Plymouth Park, Rest Stop\r\nL out of parking lot\r\nCross over Hwy 49 (regroup if necessary)\r\nL Shenandoah Rd.\r\nR Shenandoah School Rd.\r\nR Shenanoah Rd. becomes Mt. Aukum Rd.\r\nR Omo Ranch Rd.\r\nR at stop sign for Hwy 88\r\nR Shake Ridge Rd.\r\nSTRAIGHT Rams Horn Grade at Daffodil Hill (This is a great Miata Road, use caution and go at your comfort level. Watch for oncoming traffic in the turns)\r\nL stop sign in Volcano\r\nVeer LEFT Pine Grove/Volcano Rd.\r\nVeer RIGHT Pine Grove/Jackson Rd.\r\nR onto Indian Grinding Rock State Park','\r',char(13)),'\n',char(10)),replace(replace('<b>departure Routes</b>\r\n<p>\r\n<b>Quick Route</b><br>\r\nExit park to your right, at Hwy 88 turn right to Jackson, then turn right on Hwy 49 and head north to Sacramento.<br>\r\n<p>\r\n<b>Fun Route</b><br>\r\nExit park to your right, at Hwy 88 turn right, go 1/2 mile turn right on Ridge Rd. follow to Sutter Creek, turn right on Hwy 49 and head north to Sacramento.<br>\r\n<p>\r\n<b>Adventuresome Route</b><br>\r\nExit park to your left, turn left on Sutter Creek-Volcano Rd.  Comes out at north end of Sutter Creek.  Go right on Hwy 49 and head north to Sacramento.\r\n','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(16,'Chilrden''s Toy Run 2000','2000-12-09','Jack Parker','SAMOA members were invited to donate toys and other items to the Sacramento Children''s Home.  Items were delivered by the Parkers.  Start: Taco Bell in Rancho Murieta, Hwy 16.  End is lunch at the Cliff House Restaurant in Folsom.',replace(replace('R on Hwy 16\r\nR on Stonehouse (50 yds)\r\nL onto Latrobe Rd. (no choice)\r\nR on Scott Rd. (75 yds)\r\nR on Whiterock Rd.\r\nR on Latrobe Rd.\r\nL on Old Sacramento Rd.\r\nThrough Plymouth\r\ncross Hwy 49 onto Fiddletown Rd.\r\nStay RIGHT on Fiddletown Rd.\r\nIn Fiddletown turn LEFT on Tyler\r\nStay RIGHT onto Tyler (50 yds)\r\nL at "T" intersection\r\nL at Cedar Creek\r\nR on Mt. Aukum Rd.\r\nL on Bucks Bar Rd.\r\nL on Pleasant Valley Rd.\r\nSTRAIGHT through El Dorado\r\nL on Mother Lode Dr.\r\nR on South Shingle Rd.\r\nOver Freeway\r\nPast North Shingle Rd.\r\nOnto Ponderosa Rd.\r\nR on Green Valley Rd.\r\nL on Lotus Rd.\r\nLotus becomes Green Valley Rd.\r\nR on Natoma\r\nR on Folsom Dam Rd.\r\nL on Auburn-Folsom Rd.\r\nL on Folsom Blvd.\r\nR into Cliff House parking lot (50 yds)\r\n','\r',char(13)),'\n',char(10)),'The Cliff House is located in Folsom between the Rainbow Bridge and New Folsom Bridge, overlooking the American River.','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(17,'President''s Day Run','2002-02-17','Mike DeLaurentis','Start: Newcastle Denny''s, Hwy 80 at Newcastle exit, south side of freeway.  End is at Bridgeport State Park.  Donation requested: $2 per vehicle.',replace(replace('0.0|I-80 East\r\n7.5|Bell Rd., turn left\r\n7.6|Haines Rd., turn right\r\n8.5|Dry Creek Rd., turn left\r\n11.4|State Route 49, turn right\r\n17.3|Combie Rd., turn right\r\n18.1|Becomes Magnolia Rd. at Lake of the Pines\r\n23.1|Dogbar Rd., turn left\r\n34.4|Becomes La Barr Meadows Rd.\r\nMcNight St., turn right (PIT STOP at Chevron)\r\nAuburn St., turn right\r\n35.9|West Main St., turn left\r\n37.5|Becomes Rough and Ready Hwy\r\n43.0|State Route 20, turn right\r\n45.0|Pleasant Valley Rd., turn right\r\nPass Lake Wildwood on right\r\n52.7|Bridgeport State Park, turn left','\r',char(13)),'\n',char(10)),replace(replace('<b>Return</b>\r\n<p>\r\nExit the park onto Pleasant Valley Rd., turn left<br>\r\nState Route 49, turn right (59.7 mi)<br>\r\nNevada City (70.5 mi)','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(18,'Children''s Toy Run 2001','2001-12-09','Jack Parker','SAMOA members were invited to donate toys and other items to the Sacramento Children''s Home. Items were delivered by the Parkers. Start: Rancho Murieta, off Hwy 16. End is lunch at the Apple Cafe on Hwy 50, east side of Placerville.',replace(replace('Exit Rancho Murieta and turn left (east)\r\nFollow Jackson Hwy to Latrobe Rd., turn left\r\nFollow Latrobe Rd., it becomes El Dorado Hills Blvd.\r\nFollow El Dorado Hills Blvd., it becomes Salmon Falls Rd.\r\nContinue on Salmon Falls to Hwy 49, turn right\r\nFollow Hwy 49 to Coloma, park in second lot on the left near mill\r\nPIT STOP\r\nMeet back at parking lot\r\nPlan A: Turn right out of lot and turn right onto Marshall Rd.; continue up Marshall; turn right onto Garden Valley Rd.; turn right onto Georgetown Rd.; turn left onto Hwy 49; turn left onto Hwy 50; stop at Apple Cafe.\r\nPlan B: Turn left out of lot and follow Hwy 49 into Placerville; turn left onto Hwy 50; Stop at Apple Cafe.\r\nPlan C: Turn left out of lot and follow Hwy 49 to Cold Springs Rd.; turn right onto Cold Springs Rd.; follow Cold Springs Rd. to Placerville Dr.; turn right onto Placerville Dr.; go east on Hwy 50; stop at Apple Cafe.','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(19,'Old Car and River Tour','2002-05-11','Gus & Ev Young',replace(replace('Tour the countryside with a stop at Ev & Gus'', lunch in Walnut Grove and a tour of the Towe Auto Museum.\r\n<p>\r\nThe start is at the McDonald''s at Sunrise and White Rock.','\r',char(13)),'\n',char(10)),replace(replace('R White Rock Rd.\r\nL At T - stay on White Rock Rd.\r\nR Scott Rd.\r\nL Latrobe Rd. - almost immediate\r\nR Stone Creek Rd.\r\nL Jackson Hwy. (re-group past stoplight)\r\nR Ione Rd.\r\nL Hwy 104 (approx. 30 miles so far)\r\nR Five-Mile Rd. - becomes Old Stockton Rd. at left turn. (Don''t go straight - dead end)\r\nS Jackson Valley Rd. - be careful crossing Hwy 88 (re-group after crossing)\r\nR Commanche Rd. (sigh before says - Commanche Lake 4 mi.)\r\nR Commanche Parkway\r\nS Cross Hwy 88 (again - careful) Becomes Liberty Rd. (re-group after crossing)\r\nR Elliott - note animal & ostriches on right just before bridge over Dry Creek.\r\nL Simmerhorn Rd.\r\n-| Pit stop at Gus & Ev''s (approx. 62 miles total)\r\nR out of Young''s driveway\r\nL Atla Mesa\r\nR Liberty (stop sign)\r\nL Old Sacramento Rd.\r\nR Peltier Rd. (after crossing Mokelumne River note Spanish style building on right. That is the shop! The house is way back - Roman style with 5000-sq. ft. guesthouse)\r\nR Blossom Rd. (only way to go)\r\nL Walnut Grove Rd.\r\nS Through stop sign at bridge. Lunch stop on right about two blocks down. Pizza Factory - just past Ice Cream store. They have more than pizza. Sandwiches, salads, soups, etc. Park on levee Rd. or down behind - wherever you can.\r\nLeave lunch going back the way you came.\r\nR cross Sacramento River\r\nL after crossing, re-group on west side of river\r\nR Hwy 220 (sign Hogback Rd. / Ryer Island)\r\nR up onto levee - stop and wait for ferry\r\nAfter leaving ferry\r\nR Ryer Island East Rd. (To Sacramento)\r\nR cross bridge\r\nL followed by immediate\r\nR Hwy. 84\r\nR road bends 90 degrees right - no other choice\r\nS do not take 84 North - continue straight to river\r\nL follow river north. When road leaves river and drops down to left be ready to turn right\r\nR under RR. This is South River Rd. Do not take Gregory Rd.\r\nR Jefferson Blvd.\r\nR Capitol Freeway. (cross Sacramento River on Capitol Bridge)\r\nR 3rd St. (in front of Crocker Museum)\r\nL Front St.\r\nR Towe Museum parking.','\r',char(13)),'\n',char(10)),replace(replace('THE END  62 + 70 = 132 miles total\r\n<p>\r\nA SAMOA membership card gives free access to the museum.\r\n<p>\r\nThanks for joining us. Hope the tour was enjoyable. Ev and Gus Young','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(20,'2nd Annual Single Leaf Spin','2001-03-11',' Doug and Roxanne Pryor','The start is at McDonald''s at Sunrise and White Rock Rd. The finish is at the Single Leaf Winery.',replace(replace('RIGHT on White Rock Road to Grantline Road (4.6 miles)\r\nLEFT on Grantline jog to WiteRock (2.7 miles)\r\nRIGHT on Scott Road to Latrobe Road (7.6 miles)\r\nLEFT on Latrobe for a short jog to Stonehouse\r\nRIGHT on Stonehouse to Highway 16 - Jackson Highway (1.5 miles)\r\nLEFT  on Jackson Highway to <u>Rancho Murietta Country Store</u> (0.2 miles)\r\n-|<b><center>-- PIT STOP --</center></b>\r\nRIGHT on Jackson Highway to Old Sacramento Road (9.0 miles)\r\nLEFT on Old Sacramento Road to Latrobe Road (1.4 miles)\r\nLEFT on Latrobe Road to South Shingle Springs Road - downtown Latrobe (6.0 miles)\r\nRIGHT on South Shingle Springs Road to Brandon Rd. (after Memory Lane) (2.0 miles)\r\nLEFT on French Creek Road (at "T") to Old French Road (stop sign) 4.3 miles)\r\nRIGHT on Old French Road to Mother Load (2.6 miles)\r\nRIGHT on Mother Load to Pleasant Valley Road (1.1 miles)\r\nBEAR RIGHT on Pleasant Valley to <u>Tower Mart</u> (2.2 miles)\r\n-|<b><center>-- PIT STOP --</center></b>\r\nRIGHT on Pleasant Valley Rd to Bucks Bar Rd (at Clifton and Warren Store) (5.5 miles)\r\nRIGHT on Bucks Bar to Mt Aukum Road (4.6 miles)\r\nRIGHT on Mt. Aukum Road to Omo Ranch Road (7.2 miles)\r\nLEFT on Omo Ranch Road to Slug Gulch Road (at Fire Station) (8.5 miles)\r\nLEFT on Slug Gulch Road to Perry Creek Road (5.1 miles)\r\nRIGHT on Fairplay Road to <u>Gray''s Corner</u> (0.1 miles)\r\n-|<center><b>-- PIT STOP --</b><br>(with two choices and running water)</center>\r\nLEFT on Fairplay Road to Marestail Road (1.9 miles)\r\n\r\n-|<center>!!!!!WELCOME TO SINGLE LEAF!!!!!<br>our hosts are<br><b>Scott and Pam Miller</b></center>\r\n','\r',char(13)),'\n',char(10)),replace(replace('Short dirt (mud) driveway to parking area - PLEASE park in the lower lot as much as possible.\r\n<p>\r\nTotal trip to Single Leaf is 76 miles','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(21,'3rd Annual Single Leaf Spin','2002-04-21','Doug and Roxanne Pryor','The start is at MacDonald''s at Sunrise and White Rock Rd. The finish is at the Single Leaf Winery',replace(replace('RIGHT on White Rock Road to Grantline Road (4.6 miles)\r\nLEFT on Grantline jog to WiteRock (2.7 miles)\r\nRIGHT on Scott Road to Latrobe Road (7.6 miles)\r\nLEFT on Latrobe for a short jog to Stonehouse\r\nRIGHT on Stonehouse to Highway 16 - Jackson Highway (1.5 miles)\r\nLEFT  on Jackson Highway to <u>Rancho Murietta Country Store</u> (0.2 miles)\r\n-|<b><center>-- PIT STOP --</center></b>\r\nRIGHT on Jackson Highway to Old Sacramento Road (9.0 miles)\r\nLEFT on Old Sacramento Road to Latrobe Road (1.4 miles)\r\nLEFT on Latrobe Road to South Shingle Springs Road - downtown Latrobe (6.0 miles)\r\nRIGHT on South Shingle Springs Road to Brandon Rd. (after Memory Lane) (2.0 miles)\r\nLEFT on French Creek Road (at "T") to Old French Road (stop sign) 4.3 miles)\r\nRIGHT on Old French Road to Mother Load (2.6 miles)\r\nCROSS Mother Lode to Greenstone Road (0.1 miles)\r\nSTRAIGHT on Greenstone to Green Valley (2.5 miles)\r\nRIGHT on Green Valley (1.7 miles)\r\nBEAR LEFT to remain on Green Valley to Placerville Rd. (1.7 miles)\r\nRIGHT on Placerville Rd., across Hwy 50 street name changes to Ray Lawyer to Forni (0.8 miles)\r\nLeft on Forni at "T" to Main Street (1.0 miles)\r\nBEAR RIGHT on Main Street to Hwy 49 (0.2 miles)\r\nRIGHT on Hwy 49 to Pleasant Valley Rd (2.9 miles)\r\nLEFT on Pleasant Valley to <u>Happy Mart</u> (0.2 miles)\r\n-|<b><center>-- PIT STOP --</center></b>\r\nRIGHT on Pleasant Valley Rd to Mt Aukum Rd (9.1 miles)\r\nRIGHT on Mt. Aukum Road to Omo Ranch Road (11.2 miles)\r\nLEFT on Omo Ranch Road to Slug Gulch Road (at Fire Station) (8.5 miles)\r\nLEFT on Slug Gulch Road to Perry Creek Road (5.1 miles)\r\nCROSS Fairplay Road to <u>Prairie County Park</u>\r\n-|<b><center>-- PIT STOP --</center></b>\r\nRIGHT on Fairplay to Marestail Rd. (1.9 miles)\r\n-|\r\n-|<center>!!!!!WELCOME TO SINGLE LEAF!!!!!<br>our hosts are<br><b>Scott and Pam Miller</b></center>\r\n\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Short dirt driveway to parking area - PLEASE park in the lower lot as much as possible.\r\n<p>\r\nTotal trip to Single Leaf is 89 miles','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(22,'Pony Express Run','2002-06-29','Jim L. of BayAreaZ3','This drive starts in Placerville and follows some of the old Pony Express routes. It passes by a number of Pony Express stations and finishes in Angles Camp',replace(replace('Start at Buttercup Pantry in Placerville\r\nLeave Placerville on South Hwy. 49\r\nLEFT on Pleasant Valley Rd.  (Diamond Springs)\r\nRIGHT on Bucks Bar Rd. (heading to Somerset)\r\nRIGHT on Mt. Aukum Rd. past River Pines\r\nLEFT on Ostrom Rd.\r\nLEFT Fiddletown Rd.\r\nRIGHT Hale Rd.\r\nLEFT Shake Ridge Rd.\r\nRIGHT Rams Horn Grade (into Volcano where it turns into Consolation St)\r\nLEFT on Volcano Pioneer Rd.\r\nLEFT on Hwy. 88 East (towards Pioneer)\r\nSTRAIGHT past Pioneer Station\r\nSTRAIGHT past Black''s Station\r\nSTRAIGHT past Diamond Station\r\nSTRAIGHT past Cook''s Station\r\nSTRAIGHT past Ham''s Station\r\nRIGHT on Hwy. 89 (at Woodfords)\r\n-|Lunch break at Markleeville, Continue on Hwy. 89 after lunch\r\nSTRAIGHT on Hwy. 4 South (89 turns into 4)\r\nSTRAIGHT over Ebbits Pass\r\nSTRAIGHT past Alpine Lake\r\nSTRAIGHT past Bear Mountain\r\nSTRAIGHT past Big Trees\r\nSTRAIGHT through Arnold\r\nRIGHT on Hwy. 49 (this is a guess)\r\nFinish at Rod''s Grill in Angles Camp\r\n','\r',char(13)),'\n',char(10)),'Most authorities agree that Placerville and Volcano were Pony Stations. Since the stations for horse changes were about 10-15 miles apart, there had to be intermediate stops along the way. Who knows if we have the correct route, the Pony Express route changed as better routes became available and as the Indians forced the riders to find other ways around',replace(replace('The final recon is complete. The route is set. We start in Placerville at the Buttrecup Pantry , with breakfast at 10:AM. (8:AM departure from the Buttercup Grill in Walnut Creekfor anyone that wants to caravan) If you take the Historic Downtown Placerville offramp, just before the first stoplight on I50 going east from Folsom, it puts you on the correct street. Just go about two blocks to the Buttercup Pantry. The parking lot is before you get to the Buttercup. \r\n<p>\r\nThe drive will go south on Hwy 49 to Diamond Springs where we will leave the "red lines" for some fun driving.We go east on Pleasant Valley Rd, then south on Bucks Bar Rd to Somerset. From here we continue south on the Mt. Aukum Rd past River Pines, then through the wine country on Ostrom Rd to the Fiddletown Rd. We''ll take Hale Rd as a shortcut Shake Ridge Rd, then east to the Ram Horh Gradeand down into Volcano. \r\n<p>\r\nMost authorities agree that Placerville and Volcano were Pony Stations. Since the stations for horse changes were about 10-15 miles apart, there had to be intermediate stops along the way. Who knows if we have the correct route, the Pony Express route changed as better routes became available and as the Indians forced the riders to find other ways around. \r\n<p>\r\nFrom Volcano we pick up Hwy 88 at Pioneer. From here on up the grade, we will see stations about every 15 miles, Pioneer Station, Black''s Station, Diamond Station, Cook''s Station, Ham''s Station etc. It is not clear if these were Pony Stations. They were Overland Stage Stations, and were probably used by the Pony as well. After we crest Carson Pass, we will drop down to Woodfords which the experts agree was a Pony Station. \r\n<p>\r\nAt Woodfords, we head south on Hwy 89 and stop at Markleeville for lunch. We continue South to Hwy 4, then west over Ebbits Pass, past Alpine Lale, Bear Mountain, Big Trees, Arnold and down to Angles Camp. We will have a final stop at Rod''s Grill before heading our separate ways. \r\n<p>\r\nShould be a fun drive, a little history, a lot of twisties, good fellowship. See you there on the 29th. We leave Placerville at 11:AM. Please sign up so we know when everyone is there.\r\n<p>','\r',char(13)),'\n',char(10)),'http://home.pacbell.net/bdougr/pony/map.gif',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(23,'One Lap for Charity','2002-06-29','Fran Rowell',replace(replace('SAMOA supports SOCALM''s Edd Hendricks in the One Lap for Charity event.  Cost: $15 per person for pizza and salad bar.  Proceeds benefit Easter Seals and the Autism Society.\r\n\r\nStart: McDonald''s at White Rock Rd. and Sunrise Blvd.  End: Round Table Pizza, near the Madison and Greenback split, Folsom.','\r',char(13)),'\n',char(10)),replace(replace('L White Rock Rd.\r\n6.2|L Sunrise Blvd.\r\n19.0|L Hwy 16 (Jackson Hwy)\r\n16.8|L Latrobe Rd.\r\nPIT STOP at McDonald''s (Latrobe Rd. at Hwy 50)\r\nUnder Hwy 50\r\n4.0|Straight on El Dorado Hills Blvd.\r\n3.1|R Green Valley Rd.\r\n7.0|L Deer Valley Rd.\r\n10.6|R Green Valley Rd.\r\n1.0|R Natomas\r\n2.3|R Folsom Dam Rd.\r\n1.5|L Folsom Auburn Rd.\r\n1.5|R Greenback Ln.\r\nKeep straight to Madison Ave.\r\nR Round Table Pizza (before you reach Main Ave.)\r\n','\r',char(13)),'\n',char(10)),'',replace(replace('Questions:<br>\r\n1. Name your favorite 50''s song<br>\r\n2. Name your favorite 60''s song<br>','\r',char(13)),'\n',char(10)),'',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(24,'Presidential Run','','Robin & Jeanne George','Start is at the Cameron Park McDonalds (Hwy 50 at Cameron Park exit, south of the freeway).  End: Wildwood BBQ in historic Folsom.',replace(replace('L out of parking lot\r\nR onto Cameron Park Dr./Durock Rd.\r\nL on South Shingle Rd.\r\nImmediate R on Mother Lode Rd. (will regroup after turn)\r\nVeer R on Pleasant Valley Rd./Hwy 49 (towards El Dorado/Diamond Springs\r\nL at Hwy 49 (Diamond Springs)\r\nL on Pacific St.\r\nFollow Hwy 49 signs across Hwy 50\r\nR at junction 193 - towards Georgetown\r\nR at Rock Creek Rd.\r\nR at Mosquito Road Cutoff\r\nRegroup\r\nEnter Hwy 50 west\r\nExit Missouri Flat Rd. - regroup\r\nL at Green Valley Rd.\r\nR at East Natoma St.\r\nR at Riley\r\n\r\nPass through light at Sutter St., first right trn after light into paved parking lot\r\n\r\nFrom paved parking lot, walk towards Sutter St. Turn right, cross street.\r\n\r\nWildwood BBQ Co. 703 Sutter St.','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(25,'Takin'' Pictures (master)','2002-09-21','Bob & Genny Paauwe',replace(replace('Start is at the Park and Ride in El Dorado Hills. From Hyw 50, exit Latrobe Rd. South. Left at White Rock Rd. Left at Post St. (in front of Post Office). The Park and Ride is on your left.\r\n','\r',char(13)),'\n',char(10)),replace(replace('Right out of parking lot.\r\n0.00|Right at STOP (White Rock Rd.). Enter your odometer reading on line <b><u>A</u></b> of your scorecard.\r\n0.10|Left at Signal (Latrobe Rd).\r\n0.99|Straight at Signal (Golden Foothill Parkway).\r\n2.24|Straight at Signal (Investment Blvd.)\r\n5.89| -> Picture 1 - Well & Bridge on Right\r\n8.09|Left onto Latrobe Tri.\r\n8.21| -> Picture 2 - Old lean-to Right\r\n8.44|Left at YIELD (South Shingle).\r\n10.19| -> Picture 3 - Old rail road tracks that curve to the left\r\n10.49|Right onto Brandon Rd.\r\n12.49| -> Picture 4 - windmill on left\r\n12.69|Left at T (French Creek Rd)\r\n13.57| -> Picture 5 - Small barn with windows on right\r\n16.14| -> Picture 6 - Cattle ramp\r\n16.59| -> Picture 7 - Mailbox on left\r\n17.29|Straight/Right at STOP (Old French Town Rd.)\r\n20.14|Right onto Mother Lode Dr.\r\n21.20|Left onto Davidson Rd.\r\n21,40| -> Picture 8 - Old house on left past RRxing\r\n22.57|Right onto Greenstone Rd.\r\n24.30|Left onto Green Valley Rd.\r\n25.03| -> Picture 9 - Gazzebo on left\r\n27.07|Bear Right onto Lotus Rd.\r\n30.84|Right onto Thompson Hill Rd.\r\n33.19|Left onto Cold Springs Rd.\r\n34.31| -> Picture 10 - Vineyard House Sign on left\r\n34.50|Straight at STOP, onto Coloma Rd (Hyw. 49)\r\n34.94| -> Picture 11 - Chinese store on left\r\n36.20|Right onto Marshall Rd.\r\n36.79|Left onto Prospectors Rd.\r\n39.58|Left onto Marshall Rd.\r\n41.09|Right at STOP onto Garden Valley Rd.\r\n41.64| -> Picture 12 - Old truck on right\r\n44.79|Right at T onto Georgetown Rd. (Hyw. 193)\r\n45.04|Left onto Spanish Flat Rd. (1st left, comes up quick!)\r\n46.10|Right onto Shoofly Rd. (Go Slow, enjoy the ride)\r\n49.59|Left at T onto Georgetown Rd. (Hyw.193)\r\n55.34|Left onto Hyw. 49. Enter your odometer reading on line <b><u>B</u></b> of your scorecard.\r\n55.99|Right at Spring St. (stay on Hwy. 49).\r\n56.09|Straight at Signal (cross Hwy. 50)\r\n56.19|Left on Main St.\r\n56.54|Right in to Round Table Parking lot (overflow parking on left)','\r',char(13)),'\n',char(10)),replace(replace('Finish is at Round Table Pizza at 512 Main street in Placerville. The phone number is (530) 622-2259.  If you want to call ahead for your order, you can.\r\n<p>\r\nPlease try to arrive 12:30 and 1:30. Fill out your score card and turn it in. We''ll do the scoring and hand out prizes around 2:30. Please let us know what you think of this type of event by filling out the comment section on the scorecard.\r\n<p>\r\nThanks for attending,<br>\r\n<i>Your Rallymasters</i>','\r',char(13)),'\n',char(10)),replace(replace('5.89| -> Picture 1 - Well & Bridge on Right\r\n8.21| -> Picture 2 - Old lean-to Right\r\n10.19| -> Picture 3 - Old rail road tracks that curve to the left\r\n12.49| -> Picture 4 - windmill on left\r\n13.57| -> Picture 5 - Small barn with windows on right\r\n16.14| -> Picture 6 - Cattle ramp\r\n16.59| -> Picture 7 - Mailbox on left\r\n21,40| -> Picture 8 - Old house on left past RRxing\r\n25.03| -> Picture 9 - Gazzebo on left\r\n34.31| -> Picture 10 - Vineyard House Sign on left\r\n34.94| -> Picture 11 - Chinese store on left\r\n41.64| -> Picture 12 - Old truck on right\r\n','\r',char(13)),'\n',char(10)),'',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(26,'Sugar Pine Picnic','2002-07-20','Barbara & Larry Penfield','Start at McDonalds on White Rock and Sunrise.  Easy run to Sugar Pine Reservoir.',replace(replace('RIGHT out of parking lot onto White Rock Rd.\r\nSTRAIGHT at STOP\r\nLEFT at T (Grant Line Rd.)\r\nSTRAIGHT at STOP (Prairie City Rd.)\r\nLEFT at SIGNAL (Latrobe Rd.)\r\nSTRAIGHT at next 6 or 7 SIGNALS\r\nSTRAIGHT at STOP\r\nSTRAIGHT at SIGNAL (Green Valley Rd.) Becomes Salmon Falls Rd.\r\nRIGHT on HWY. 49\r\nLEFT on Marshall Rd.\r\nSTRAIGHT at STOP (Garden Valley Rd.)\r\nLEFT onto Greenwood Rd..\r\nLEFT at STOP (HWY. 193)\r\nAT HWY. 49, go straight into shopping center\r\n-|We''ll take a 15 to 20 minute break here\r\nLEFT on HWY 49 out of parking lot\r\nRIGHT at STOP (Foresthill-Auburn Rd.)\r\nRIGHT at STOP (Foresthill Rd.)\r\nLEFT on Sugar Pine/Iowa Hill Rd.\r\nRIGHT into Sugar Pine Reservoir Park\r\n-|Cost is $3 to enter the park. This is the end of the offical route. Time for a Picnic!!','\r',char(13)),'\n',char(10)),replace(replace('Suggested routes to get back to Sacramento.\r\n<p>\r\n<table border=1 cellspacing=5 cellpadding=3>\r\n<tr>\r\n<td valign=top>\r\nThe VERY scenic and fun route!\r\n<p>\r\nExit the park and turn RIGHT onto Iowa Hill Rd.<br>\r\nRoad will become very narrow (but paved)<br>\r\nCross the bridge and continue until you come to a T<br>\r\nTurn LEFT<br>\r\nThe road parallels I-80<br>\r\nTake the on-ramp for I-80 west and head home<br>\r\n</td>\r\n<td valign=top>\r\nThe boring way home:\r\n<p>\r\nExit park and turn LEFT onto Iowa Hill Rd.<br>\r\nRIGHT onto Foresthill Rd. (at T)<br>\r\nConutinue on Foresthill Rd. to I-80<br>\r\n</td>\r\n</tr>\r\n</table>','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(27,'Passenger Swap','1999-03-14','Hugo & Noelle Schmidt','Start is at Starbucks at Truxel and I-80 (near In-N-Out Burger).  At intervals, passengers rotate to the car behind them.  End is at Hueys Diner in West Sacramento.',replace(replace('L at Truxel Rd. light\r\nL at Del Paso Rd. (will regroup after turn)\r\nL at El Centro Rd.\r\nL at West El Camino Rd.\r\nR at Orchard Rd.\r\nR at Garden Hwy\r\nJust past Rio Ramada Resort at the Y -- SWAP #1\r\nNicolas post office -- SWAP #2\r\nU-Turn and LEFT onto Hwy 99\r\nL at 113 (Woodland sign)\r\n(Regroup by farm equipment sign)\r\nKnights Landing, SWAP #3 at gas station/market (bathroom stop also)\r\nL onto E8\r\nR onto I-5\r\nR at RD22 exit to West Sacramento -- SWAP #4 -- inthe court of the road (15 minutes from Hueys)\r\nR on Old River Rd.\r\nCross under I-5 and RIGHT on North Harbor Blvd.\r\nL at West Capitol\r\nR into parking lot by Burger King\r\nEND at Hueys Diner\r\n','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(28,'Nagavator''s Run to Bodega Bay','2000-10-14','JR & Susie Hahn/Chuck & Vicki Knorr',replace(replace('Start is at the McDonald''s on Chiles Rd., Davis.  End is at the Sonoma Coast State Beach.\r\n\r\nWe are about to embark on a grand adventure to Bodega Bay.  There are several tricky turnoffs and places to get off on the wrong road.  Please try to keep the person in front of you in view and the person in back of you in view so we don''t get separated.  We will do our best to set a comfortable pace and regroup when necessary.  Drive safely, have fun on the twisties and we hope you enjoy this somewhat longer than usual day run.','\r',char(13)),'\n',char(10)),replace(replace('Turn RIGHT exiting McDonald''s parking lot onto Chiles Rd.\r\nLEFT at 1st light (onto Mace Blvd.) and get back on I-80 Westbound (toward SF)\r\nExit northbound onto Hwy 113\r\nExit westbound onto Russell Rd.\r\nVeer LEFT and stay on Russell Rd. where it meets Arlington Rd.\r\nContinue on Russell Rd. until you come to a T intersection with E-8\r\nTurn LEFT onto E-8 which is now called Russell Blvd. (or still called Russel Rd. on some maps.  Not to worry, you haven''t even gotten to the hard part yet!)\r\nContinue on Russell Blvd. toward the town of Winters (Russell will eventually turn into Hwy 128 about the time you cross I-505)\r\nContinue on Hwy 128 to fork in the road with Hwy 121\r\nTurn RIGHT onto Berryessa/Knoxville Rd. where we will take a pitstop at Moskowite''s (we have stopped there on other runs, you''ll probably recognize it)\r\nTurn LEFT out of parking lot onto Berryessa/Knoxville Rd. (we are returning to the insection of Hwy 128 and Hwy 121)\r\nTurn RIGHT onto Hwy 121 (watch for local traffic and enjoy the road.  Are we having fun yet?)\r\nStay on Hwy 121 South and it will turn into the old Silverado Trail (now it will get a little tricky, pay attention and try to stick together)\r\nTurn RIGHT onto Imola Ave. (just past where Silverado Trail is joined by Soscol Ave.).  Note that you are now going westbound but it is still Hwy 121\r\nStay on Imola Ave. until you get to Hwy 29 (which we will go under)\r\nImmediately after crossing under Hwy 29 we will turn LEFT onto Golden Gate Dr. (where we will probably regroup)\r\nTurn LEFT onto Stanley Lane until you get to intersection with Hwy 121\r\nTurn RIGHT onto Hwy 121 westbound (a.k.a. Carneros Hwy and Hwy 12 at this point.  Confused?  You should have seen us the first time we tried it!)\r\nVeer LEFT and stay on Hwy 121\r\nTurn RIGHT onto Hwy 116 toward Petaluma\r\nVeer LEFT and stay with Hwy 116 (continuing toward Petaluma)\r\nStay with Hwy 116 as it jogs RIGHT and crosses Hwy 101.\r\nWatch for a McDonald''s and Albertsons on the RIGHT (this is another pitstop at the McDonald''s)\r\nGet back on Hwy 116 (no we''re not there yet...) continuing westbound\r\nTurn LEFT onto East Washington\r\nVeer RIGHT onto Bodega Ave. and follow until it becomes the Bodega Hwy (trust us, it will take you to the town of Bodega, Bodega Bay and Hwy 1 north, toward Jenner)\r\nOnce on Hwy 1 northbound follow it to Sonoma Coast State Beach\r\nTurn LEFT onto the Sonoma Coast State Beach road and follow it to the beach.  When you have a chance to go left or right, stay left, toward Blind Beach and Goat Rock.','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(29,'Kokanee Salmon Run','2001-10-06','Rod Gonzales','Start is at Cameron Park McDonald''s (Hwy 50 at Cameron Park Exit, south of the freeway).  Finish at the Taylor Creek Visitor''s Center, near South Lake Tahoe.',replace(replace('Exit McDonald''s parking lot, turn RIGHT onto Coach Lane to Cameron Park Dr.\r\nTurn RIGHT onto Cameron Park Dr.  Continue south on Cameron Park Dr. which at about 0.2 miles will curve left, becoming Durock Rd.\r\nContinue east on Durock Rd. to S. Shingle Springs Rd.\r\nAt the signal, turn LEFT onto S. Shingle Springs Rd., then immediately turn RIGHT onto Mother Lode.  We will regroup along Mother Lode if necessary\r\nContinue on Mother Lode through the town of Shingle Springs.  Remain on Mother Lode until it forks to the right onto Pleasant Valley Rd.\r\nStay tot the RIGHT on Pleasant Valley Rd; there will be a stop sign where it meets Hwy 49. Continue straight on Pleasant Valley Rd./Hwy 49 to Diamond Springs.\r\nIn Diamond Springs, Pleasant Valley Rd. and Hwy 49 split at the stop sign; continue straight on Pleasant Valley Rd. for another 4.5 miles, where there will be another stop sign and the intersection of Bucks Bar Rd.  Continue straight on Pleasant Valley Rd. for about 5 miles to the town of Pleasant Valley\r\nAt Pleasant Valley, Pleasant Valley Rd. ends at CR E16.  Turn left onto CR E16, a.k.a. Sly Park Rd.  Stay on Sly Park Rd.  Continue on Sly Park Rd. for about 3.5 miles to Mormon Emigrant Trail Rd.  Turn RIGHT onto Mormon Emigrant Trail Rd.  and continue to Hwy 88\r\nAt the stop sign, turn LEFT onto Hwy 88; we will regroup along Hwy 88 if necessary\r\nWe will resume on Hwy 88 for about 5.5 miles where we will PIT STOP at Kay''s Resort for 15-20 minutes.  You may want to put on a jacket, sweater or sweatshirt if you haven''t already, before we continue; it can get real chilly on this leg from here to Hwy 89\r\nAfter our pit stop, we will will get back onto Hwy 88 and continue for about 21 miles to Hwy 89\r\nTurn LEFT onto Hwy 89 and continue for 11 miles or so where Hwy 89 will meet Hwy 50\r\nTurn RIGHT onto Hwy 50.  We will continue for about 5 miles until we arrive at the "Y" where Hwy 50 and Hwy 89 split.  At the "Y", we will continue straight on Hwy 89 (a.k.a. Emerald Bay Rd.) for about 3.25 miles to the Taylor Creek Visitor''s Center.\r\nWe have arrived...enjoy!','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(30,'Graeagle - Plumas Eureka State Park','2001-08-25','Bob & Denise Land','Start is at the Auburn Raley''s (Foresthill offramp from I-80 and Lincoln Way).  Finish is at Plumas Eureka State Park in Graeagle.',replace(replace('.6|Turn RIGHT from Raley''s parking lot onto Lincoln Way\r\n.1|LEFT on Bowman Rd. undercrossing\r\n1.8|RIGHT onto Bowman Rd.\r\n5.3|RIGHT on Lake Arthur Rd. (becomes Crother Rd.)\r\n5.2|RIGHT on Placer Hills Rd.\r\n2.1|Placer Hills Rd. becomes Tokayana Way\r\n.1|RIGHT on Old Tokayana Way\r\n.1LEFT on Rising Sun\r\n.1|RIGHT on Grass Valley St.\r\n.3|LEFT on Main St. through Colfax\r\n9.1|LEFT on Hwy 174\r\n3.4|RIGHT on Brunswick Rd.\r\n2.5|RIGHT onto Hwy 20/Hwy 49 to Nevada City\r\n45.0|Get in left lane to turn LEFT onto Hwy 49\r\nPit stop in Downieville for the brave only\r\n17.5|Continue east on Hwy 49 to Bassetts\r\n7.0|Hard LEFT on Gold Lake Rd.\r\n1.5|RIGHT on Graeagle Rd.\r\n6.0|Continue on Gold Lake Forest Hwy\r\n1.7|LEFT on Hwy 89\r\n3.9|LEFT on Graeagle-Johnsville Rd. to State Park\r\nEND\r\n','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(31,'Run Around the Hills with Baddtaz 2002','2002-07-27','Dennis Detloff & Barbara Fenner',replace(replace('Welcome to our 4th annual Run Around the Hills with Baddtaz!\r\n<p>\r\nStart is at the McDonalds on Cameron Park Dr. in Cameron Park.','\r',char(13)),'\n',char(10)),replace(replace('-|Gather n the parking lot\r\nTurn right onto Cameron Park Dr. This becomes Durock Rd.\r\nLeft onto Shingle Springs Rd.\r\n-|We will re-group across the freeway in front of the church\r\nLeft onto Green Valley Rd.\r\nRight onto Salmon Falls Rd.\r\nRight onto Samon Falls Cutoff\r\nRight onto Hwy 49 (Yipee a Miata Road)\r\n-|Rest Stop at the Bar on Marshall Rd and Hwy 49\r\nBack onto Hwy 49 and driver until we get to Hwy 50\r\nGo across Hwy 50 into Placerville and keep on Hwy 49\r\nCross Sacramento Str.\r\nRight onto Pacific St.\r\nTurn right onto Cedar Ravine (This is a nice Miata Road also)\r\nTurn left onto Plesant Valley Rd.\r\n-|(Pleasant Valley Road becomes Sly Park Road around the Holiday Market on the right)\r\nStay on Sly Park Road to Pony Express Trail.\r\nTurn right onto Pony Express Trail.\r\n-|Rest Stop at the 76 Stations on the left\r\nRight out of 76 Station onto Pony Express Trail (towards Camino)\r\nTurn left onto Snows Rd. (This is also a somewhat bumpy road) This is a real twisty , turny Miata road as well as the next one.\r\nTurn right onto Newtown Rd.\r\n-|Newtown Road becomes Broadway around the time you get to Placerville.\r\nTurn left onto Main St. (Broadway dead ends here)\r\nTurn left into the parking lot beside Tortilla Flats Restaurant.','\r',char(13)),'\n',char(10)),'We should be on the road about 3 1/2 hours. Hope you have a great time and enjoy the scenery.','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(32,'The Way to Fort Bragg','2001-11-10','Tony Rivas','This is an overnight stay in Fort Bragg.  Start is the McDonald''s on Harbor Blvd. in West Sacramento.',replace(replace('From the McDonald''s parking lot on Harbor Blvd., exit onto Harbor Blvd. and immediately make a LEFT onto West Capitol Ave.\r\nFollow West Captiol Ave. (west) to I-80.  Get onto I-80 Westbound and follow it to Hwy 113 in Davis\r\nTake Hwy 113 to the Covell Blvd. exit.  Turn left onto Covell Blvd. and follow it to the Chevron Station in Winters.  This will be our first potty stop to discard some of our morning coffee\r\nExit the Chevron Station and turn RIGHT onto what is now Hwy 128.  Follow Hwy 128 around Lake Berryessa to Hwy 121 (Hwy 121 is a PURE Miata Road)\r\nFollow Hwy 121 to the Silverado Trail.  Turn RIGHT onto Dilverado Trail.\r\nFollow the Silverado Trail to the Mumm Winery in Rutherford.  Turn LEFT into the parking lot (this is our second potty stop)\r\nExit the Mumm Winery and turn LEFT onto the Silverado Trail.  Follow the Silverado Trail to Hwy 29\r\nTurn LEFT onto Hwy 29 and follow it through Calistoga to Hwy 128\r\nTurn RIGHT onto Hwy 128 and follow it to Hwy 101\r\nTurn LEFT and then an IMMEDIATE RIGHT onto Hwy 101.  Follow Hwy 101 to the South Cloverdale exit.  At the stop sign, turn LEFT and go over the highway to Cloverdale Blvd.\r\nTurn LEFT onto Cloverdale Blvd. and follow to the Owl Cafe.  The Owl Cafe is our lunch stop and it will be on the LEFT side of the road.\r\nAfter lunch, get back on Cloverdale Blvd. heading north (left out of the parking lot).  Follow Cloverdale Blvd. to Hwy 101 and get onto Hwy 101 heading north to Hwy 128.  Take Hwy 128 exit.\r\nTurn west onto Hwy 128 (Hwy 128 is a PURE Miata Road).  Follow Hwy 128 (approximately 55 miles) to Hwy 1\r\nTurn RIGHT onto Hwy 1 and follow to the Pine Beach Inn (approximately 1 miles south of Fort Bragg) on the left side of the highway.\r\nEND','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(33,'Poker Fun Run','1999-08-15','Robin & Jeanne George',replace(replace('This poker run originally came with a CD or cassette tape with verbal instructions and music to listen to between those instructions.  Even without the CD/cassette, this is still a great fun run.\r\n\r\nStart is at the McDonald''s at White Rock Rd. and Sunrise Blvd.  End is at Lou La Bonte''s, 13460 Lincoln Way, Auburn.','\r',char(13)),'\n',char(10)),replace(replace('Follow the yellow line out of the parking lot (Card Stop #1)\r\nRIGHT on White Rock Rd.\r\nLEFT at T, continuing on White Rock Rd.\r\nCross Latrobe Rd.\r\nLEFT on Post Rd.\r\nRIGHT into post office customer parking lot (Card Stop #2)\r\nExit parking lot, LEFT on Post Ct.\r\nRIGHT onto Post St.\r\nLEFT on Town Center Blvd.\r\nRIGHT on Latrobe Rd.\r\ngo under Hwy 50\r\nVeer RIGHT on Serrano Pkwy.\r\nLEFT on Silva Valley Pkwy.\r\nFollow road about 3.5 miles\r\nRIGHT at Green Valley Rd.\r\nGo 2.5 miles, LEFT on Deer Valley Rd.\r\nLEFT on Green Valley Rd.\r\nLEFT at the T, continue on Green Valley Rd.\r\nLEFT at the Y, continue toward Lotus\r\nLEFT on Hwy 49\r\nRIGHT at Marshall Rd. (by Coloma Club) (Card Stop #3)\r\nRIGHT on Marshall Rd.\r\nLEFT on Prospector Rd. (.5 mile)\r\nLEFT on Marshall Rd.\r\nFollow towards Georgetown\r\nRIGHT on Lower Main St., Georgetown\r\nRIGHT on Orleans to Sheriff Satellite Office (Card Stop #4)\r\nLEFT onto Main St.\r\nTurn RIGHT at 2nd stop sign (Auburn 19 miles), this is Hwy 193\r\nRIGHT at Cool, follow toward Auburn after crossing river\r\nRIGHT at T after bridge toward Foresthill\r\nLEFT onto Foresthill Rd.\r\nRIGHT on Lincoln Way\r\nLou la Bonte''s is on the RIGHT (Card Stop #5)\r\n','\r',char(13)),'\n',char(10)),'1 hour, 42 mins driving time.','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(34,'Takin'' Pictures','2002-09-21','Bob & Genny Paauwe',replace(replace('Start is at the Park and Ride in El Dorado Hills. From Hyw 50, exit Latrobe Rd. South. Left at White Rock Rd. Left at Post St. (in front of Post Office). The Park and Ride is on your left.\r\n','\r',char(13)),'\n',char(10)),replace(replace('Right out of parking lot.\r\n0.00|Right at STOP (White Rock Rd.). Enter your odometer reading on line <b><u>A</u></b> of your scorecard.\r\n0.10|Left at Signal (Latrobe Rd).\r\n0.99|Straight at Signal (Golden Foothill Parkway).\r\n2.24|Straight at Signal (Investment Blvd.)\r\n8.09|Left onto Latrobe Tri.\r\n8.44|Left at YIELD (South Shingle).\r\n10.49|Right onto Brandon Rd.\r\n12.69|Left at T (French Creek Rd)\r\n17.29|Straight/Right at STOP (Old French Town Rd.)\r\n20.14|Right onto Mother Lode Dr.\r\n21.20|Left onto Davidson Rd.\r\n22.57|Right onto Greenstone Rd.\r\n24.30|Left onto Green Valley Rd.\r\n27.07|Bear Right onto Lotus Rd.\r\n30.84|Right onto Thompson Hill Rd.\r\n33.19|Left onto Cold Springs Rd.\r\n34.50|Straight at STOP, onto Coloma Rd (Hyw. 49)\r\n36.20|Right onto Marshall Rd.\r\n36.79|Left onto Prospectors Rd.\r\n39.58|Left onto Marshall Rd.\r\n41.09|Right at STOP onto Garden Valley Rd.\r\n44.79|Right at T onto Georgetown Rd. (Hyw. 193)\r\n45.04|Left onto Spanish Flat Rd. (1st left, comes up quick!)\r\n46.10|Right onto Shoofly Rd. (Go Slow, enjoy the ride)\r\n49.59|Left at T onto Georgetown Rd. (Hyw.193)\r\n55.34|Left onto Hyw. 49. Enter your odometer reading on line <b><u>B</u></b> of your scorecard.\r\n55.99|Right at Spring St. (stay on Hwy. 49).\r\n56.09|Straight at Signal (cross Hwy. 50)\r\n56.19|Left on Main St.\r\n56.54|Right in to Round Table Parking lot (overflow parking on left)','\r',char(13)),'\n',char(10)),replace(replace('Finish is at Round Table Pizza at 512 Main street in Placerville. The phone number is (530) 622-2259.  If you want to call ahead for your order, you can.\r\n<b>Please try to arrive between 12:30 and 1:30. Fill out your score card and turn it in.</b> We''ll do the scoring and hand out prizes around 2:30. Please let us know what you think of this type of event.\r\n','\r',char(13)),'\n',char(10)),replace(replace('5.89| -> Picture 1 - Well & Bridge on Right\r\n8.21| -> Picture 2 - Old lean-to Right\r\n10.19| -> Picture 3 - Old rail road tracks that curve to the left\r\n12.49| -> Picture 4 - windmill on left\r\n13.57| -> Picture 5 - Small barn with windows on right\r\n16.14| -> Picture 6 - Cattle ramp\r\n16.59| -> Picture 7 - Mailbox on left\r\n21,40| -> Picture 8 - Old house on left past RRxing\r\n25.03| -> Picture 9 - Gazzebo on left\r\n34.31| -> Picture 10 - Vineyard House Sign on left\r\n34.94| -> Picture 11 - Chinese store on left\r\n41.64| -> Picture 12 - Old truck on right\r\n','\r',char(13)),'\n',char(10)),'',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(35,' The Ghosts of Bridgeport','2002-10-27',' Mike & Helen DeLaurentis','A brisk drive from Newcastle to the South Yuba River State Park in Bridgeport to participate in the annual docent-lead Ghosts of Bridgeport Halloween walk.',replace(replace('Start at the Newcastle Dennys, I-80 at the Newcastle exit.\r\n\r\n0.0| I-80 East\r\n7.4| Bell Road, turn left\r\n7.6| Haines Road, turn left\r\n8.4| Dry Creek Road, turn right\r\n11.3| State Route 49, turn right\r\n17.4| Combie Road, turn right\r\n18.3| Becomes Magnolia Road at Lake of the Pines\r\n23.1| Dogbar Road, turn left ("T" intersection)\r\n34.3| Becomes LaBarr Meadows Road\r\n\r\nMcNight Street, turn right (Pit stop at Chevron)\r\n\r\n34.6| Auburn Street, turn right (stay in right lane)\r\n35.9| West Main Street, turn left ("T" intersection)\r\n37.5| Becomes Rough and Ready Highway (regroup)\r\n42.9| State Route 20, turn right\r\n44.8| Pleasant Valley Road, turn right\r\nPass Lake Wildowood on right\r\n52.5| Bridgeport State Park, turn left (park in the lot or along the street)\r\n\r\nRETURN\r\nExit the park and turn left\r\n59.7| State Route 49, turn right\r\n70.5| Nevada City\r\n\r\n','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(36,' NUMMI Tour','2003-02-12','Bob & Genny Paauwe',replace(replace('This run starts in West Sacramento and takes us to Fremont for a tour of the New United Motor Manufacturing, Inc. (NUMMI) plant. NUMMI is a joint venture between Toyota and General Motors, manufacturing 220,000 cars and 150,000 trucks annually.\r\n\r\nThe route takes you through the delta, along levees, across a CalTrans ferry and down some lesser-traveled country roads in the coastal range mountains.  Breathtaking views and secluded spots dot this route.\r\n\r\nAll mileages are approximate.\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Start is at the Chevron gas station at 1015 Jefferson Blvd. (at Jackson) in West Sacramento.\r\n\r\n0.00|Exit Chevron gas station; left onto Jefferson Blvd.\r\n17.2|Right at stop onto Courtland Rd. (becomes Ryer Rd.)\r\n28.2|Straight at 220 E -- continue on Hwy 84\r\n33.1|Right onto the Real McCoy ferry (Hwy 84)\r\nAfter you cross on the ferry, continue to the left, staying on Hwy 84 to Rio Vista.\r\n85.6| (approximate) Turn right onto Hwy 12 West.\r\n85.9| Right into McDonalds/Chevron parking -- POTTY STOP\r\nLeft out of parking lot onto Hwy 12 East (go over bridge)\r\n36.7|Right onto Hwy 160 toward Antioch (turns into Hwy 4)\r\nGo over the big Antioch bridge (no toll going this way!  Ha! Ha!).\r\n51.1|Exit Hwy 4 at Hillcrest Ave.\r\nLeft at signal (over the freeway) onto Hillcrest Ave. (turns into Deer Valley Rd.)\r\n59.9|Right at Marsh Creek Rd.\r\n67.8|Left at Morgan Territory Rd.\r\n(Use extreme caution here!  Road is one-lane most of the way.)\r\n77.1| Left into Morgan Territory Preserve parking lot -- POTTY STOP\r\n77.2| Left out of parking lot onto Morgan Territory Rd.\r\n82.7|Right on Manning Rd. at stop\r\n84.9|Left at Carneal Rd. at stop\r\n85.5|Right on Collier Canyon Rd.\r\n87.0|Left on Highland Rd. at stop\r\n89.0|Left at stop onto Camino Tassajara Rd.\r\n94.5|After crossing I-580, Camino Tassajara becomes Santa Rita Rd.\r\nGo through charming downtown Pleasanton.\r\n97.8|Left at Stop onto Bernal Ave.\r\n97.9|Right onto Sunol Blvd.\r\nGo under I-680; Sunol Blvd. becomes Pleasanton-Sunol Rd.\r\n102.9|Right at stop onto Hwy 84 (toward Sunol)\r\n109.3|Left at signal onto Hwy 84 (Mission Blvd.)\r\n109.9|Right onto Hwy 84\r\n112.3|Left onto Fremont Blvd.\r\nContinue on Fremont Blvd.  It gets tricky, so pay attention!  At a V intersection, take the right arrow, staying on Fremont (not Washington, which seems to go straight).\r\n118.0|Left into NUMMI at Industrial Ave. entrance.\r\n\r\n\r\nHow to get back to Sacramento:\r\n\r\nExit NUMMI\r\nRight onto Fremont Blvd.\r\nRight onto S. Grimmer\r\nRight on Warm Springs\r\nLeft on Mission Blvd.\r\nNorth on I-680 (toward Sacramento)\r\nTake Hwy 84 East (toward Livermore)\r\nRight onto I-580 east after going through Livermore\r\nOnto I-205\r\nNorth on I-5 back to Sacramento\r\n\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Depending on time, we will eat lunch somewhere along Fremont Blvd. near Mowry.  If we are running late, we will continue past NUMMI and pull into the McDonalds on the other side of I-880.\r\n<p>\r\nRestaurants along Fremont Blvd.: Chili''s, Elephant Bar, Subway, Round Table Pizza.\r\n<p>\r\nNUMMI:\r\n<p>\r\n45500 Fremont Blvd.<br>\r\nFremont, CA  94538<br>\r\n(510) 498-5500<br>\r\nwww.nummi.com\r\n','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(37,' Christmas Lights Run','2002-12-07',' Staci Overman','A short run (1.5 hrs.) starting at the McDonald''s at the corner of White Rock Rd. and Sunrise Blvd.  Run route is subject to change based upon light displays.  Ends at Lyon''s in Folsom.',replace(replace('Right onto White Rock Rd. from parking lot\r\n*If it is a clear night, please note the beautiful light display in the sky above you.\r\nLeft at stop sign - continue on White Rock Rd.\r\nLeft at stop sign, turning onto Prairie City Rd.\r\nLeft onto Iron Point Rd. (after Intel)\r\nRight on Ingersoll (before the outlets)\r\nRight on Natoma Station Dr.\r\nFirst right onto Parker Dr.\r\n*This has been one of my favorite neighborhoods every year.  It seems like the entire4 neighborhood gets together for Christmas.  Hopefully it will be just as grend for us.  The neighborhood is small, so I did not list any streets -- just thought we would drive around (no one should get lost!).\r\nExit the neighborhood the way we came in.  Take a right onto Natoma Station Dr.\r\nRight onto Turn Pike Dr.\r\n*The street we are on leads to many side streets which may contain Christmas wonders.  This is where we may go off the original route in search of displays.\r\nFrom Turn Pike Dr., turn right onto Blue Ravine Rd.\r\nAt the next light, turn left onto Sibley (don''t be confused by the road signs -- the street on the right is Prairie City Rd.)\r\n*Note: At the intersection of Sibley and Natoma, on-coming traffic DOES NOT STOP.  Please be careful when crossing the intersection.\r\nLeft on Reading St.\r\n*Please notice the house on the right before you turn onto Reading St. -- this is one of my favorite displays.\r\nRight onto Sutter\r\n*This is Old Town Folsom, full of wonderful shops and restaurants.  They always do a wonderful job of decorating the streets for the holidays.\r\nAt stop light, turn left onto Riley St.\r\nCross over the Rainbow Bridge\r\nGo straight at the intersection of Folsom-Auburn Rd.  The street we are currently on changes from Riley to Greenback\r\nAt the next stop light, turn right onto American River Canyon\r\nStay straight on this road, until you come to a large waterfall in front of you\r\nAt the waterfall, turn left (still American River Canyon Dr.)\r\n*This is one of the most beautiful neighborhoods in the area.  Even without the Christmas displays, the houses are absolutely gorgeous.  Did you find your dream home?  I found mine.\r\nThe road travels through the entire neighborhood.  Once we return to the waterfall, take a left.\r\nAt the stop light, turn left onto Oak Ave.\r\nAt the stop light, turn right onto Folsom-Aurburn Rd.\r\nCross over Lake Natoma Crossing -- The "new" bridge (straight at the light at Greenback)\r\nLeft onto Blue Ravine Rd.\r\nRight into the Lyon''s parking lot at the intersection of Blue Ravine Rd. and East Bidwell.\r\n\r\nEND','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(38,' Cultured Cabernets @ Sierra Vista Winer','2003-01-12','Margaret McConnell','Meet at Java Caffeina: 2228 Sunrise Blvd., north of Hwy 50 between Coloma & Gold Express.  After a light lunch, a direct route will be taken to Sierra Vista Vineyards, 4560 Cabernet Way, Placerville.',replace(replace('Right @ Sunrise\r\nHwy 50 East\r\nExit Diamond Springs, Right\r\nLeft @ Pleasant Valley Rd.\r\nStraight @ Bucks Bar Rd.\r\nStraight @ School on right\r\nRight @ Leisure Lane\r\nRight @ Cabernet Way','\r',char(13)),'\n',char(10)),'Website: http://sierravistawinery.com/','If you''re not in a hurry, back roads can be taken that avoid Hwy 50 altogether.','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(39,' 2003 Superbowl Sunday Run','2003-01-26','Rod Gonzales',replace(replace('Start is at the McDonald''s on Chiles Rd. in Davis (exit Chiles Rd. from I-80).\r\n\r\nEnd at Round Table Pizza in Winters for lunch.\r\n\r\nThis is a pretty cut-and-dry route; it''s not too complicated.  Once we join the group in Winters, we''ll only be driving on three roads -- Hwy 128, Hwy 121 and The Silverado Trail.','\r',char(13)),'\n',char(10)),replace(replace('Turn right onto Chiles Rd. from the McDonald''s parking lot\r\nTurn left (north) at the signal onto Mace Blvd.\r\nGo up Mace Blvd. and turn left onto the I-80 West ramp\r\nContinue west on I-80 to the Hwy 113 North exit (towards Woodland)\r\nContinue north on Hwy 113 to the Russell Blvd. exit\r\nStay in the left exit lane and turn left at the signal onto Russell Blvd.\r\nContinue on Russell Blvd.  Note:  Russell Blvd. bends right, then a sharp left (yield to oncoming traffic), then right.  We''ll stay on Russell Blvd. until we reach Hwy 128 (dead end at the "T")\r\nTurn left at the "T" (Stop sign) from Russell Blvd. onto Hwy 128 (towards Winters)\r\nWe will stop briefly at the Chevron gas station (Hwy 128 on the other side of I-505) to meet and take on more participants (10-15 mins.)\r\nWe''ll continue west on Hwy 128 through Winters, past Lake Berryessa to Hwy 121 (Hwy 128 bends left to become Hwy 121)  Note: Use caution driving on Hwys 128 and 121.  Recent wet conditions may loosen rock and cause it to fall onto the roadway.\r\nContinue on Hwy 121 into Napa where we will turn right (north) onto The Silverado Trail\r\nContinue north on The Silverado Trail for about 2 miles where we''ll PIT STOP at the Soda Canyon Deli/Store (30-45 mins.)\r\nAfter the pit stop, we''ll regroup and continue north on The Silverdo Trail and enjoy the scenery on the way to Hwy 128\r\nTurn right (east) onto Hwy 128\r\nContinue east on Hwy 128 through the twisties until the "T" where it meets at the Hwy 128/121 split (stop sign)\r\nTurn left at the stop sign onto Hwy 128 and continue back to Winters\r\nWe will stop in Winters at the Round Table Pizza for lunch (This is optional, but we hope everyone will find time to join us)\r\nAfter lunch, everyone is on their own to get back in time for the BIG GAME','\r',char(13)),'\n',char(10)),'GO TEAM!','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(40,' Columbia Memorial Run','2003-02-09',' Bob & Genny Paauwe',replace(replace('This run is dedicated to the astronauts that lost their lives during the STS-107 (Space Shuttle Columbia) reentry on February 1st, 2003.\r\n<p>\r\n','\r',char(13)),'\n',char(10)),replace(replace('-|Start at the park-n-ride, El Dorado Hills Blvd. and HWY 50, El Dorado Hills\r\n|RIGHT out of parking lot onto Post St.\r\n|RIGHT on White Rock Rd.\r\n|LEFT at SIGNAL onto Latrobe Rd.\r\n|STRAIGHT at STOP\r\n|LEFT at STOP onto Jackson Hwy (Next right comes up quick!)\r\n|RIGHT on Carbondale Rd.\r\n|LEFT on Irish Hill Rd.\r\n|LEFT at STOP onto HWY 104.\r\n|In Ione, RIGHT on HWY 124\r\n|LEFT into Charles Howard Park (Potty Break)\r\n|LEFT out of Park continuing on HWY 124\r\n|LEFT on Buena Vista Rd.\r\n|LEFT onto HWY 88\r\n|RIGHT onto HWY 49\r\n|Stay on HWY 49 until almost Sonora, \r\n|LEFT onto Springfields Rd.\r\n|Turns into Broadway St (or Parrots Ferry) in Columbia\r\n|Lunch (Columbia/Sonora)\r\n-|\r\n-|Return Trip -- On your own\r\n-|\r\n|RIGHT on Parrots Ferry Rd.\r\n|LEFT on HWY 4 (in Vallecito)\r\n|RIGHT on HWY 49\r\n-|\r\n-|You might want to stop at some of the towns along the way.\r\n-|Hwy 49 will take you back to Plymouth, from there you can take Hwy 16 into Sacramento, continue on Hwy 49 into Placerville, or take Latrobe to Hwy 50.\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('<h3>The Challenger Learning Center of Sacramento</h3>\r\n<p>\r\nThe monies collected on this run will be donated to the Challenger Learning Center of Sacramento. It is part of the The Sacramento Museum of History, Science, Space, & Technology. \r\n</p>\r\n<p>\r\nThe internationally acclaimed Challenger Learning Center Network currently consists of state-of-the-art, innovative educational simulators located in at 46 sites across 29 states, Canada, and the United Kingdom. Staffed by master teachers, the core of each Center is a two-room simulator, consisting of a space station, complete with communications, medical, life, and computer science equipment, and a mission control room patterned after NASA''s Johnson Space Center and a space lab ready for exploration.\r\n</p>\r\n<p>\r\nAll of the programs conducted at the Challenger Learning Centers utilize the same robust educational model that emphasizes educational content, cooperative learning, problem-solving, and responsible decision-making. \r\n</p>\r\n<p>\r\nFor more information see http://www.thediscovery.org/\r\n</p>\r\n\r\n\r\n<h3>The Columbia''s Crew</h3>\r\n<p>\r\n<img src="http://www.nasa.gov/images/content/columbia/crew/thumb_rickd.jpg" align=left width="64" height="48" alt="Rick D. Husband, Commander" border="0" />Rick D. Husband, Commander</a><br />\r\n		Rick Husband, 45, a colonel in the U.S. Air Force, was a test pilot and veteran of one spaceflight. Selected by NASA in December 1994, Husband logged more than 235 hours in space.<br clear=left />\r\n</p>\r\n<p>\r\n\r\n<img align=left src="http://www.nasa.gov/images/content/columbia/crew/thumb_williamm.jpg" width="64" height="48" alt="William C. McCool, Pilot" border="0" />William C. McCool, Pilot</a><br />\r\n		William C. McCool, 41, a commander in the U.S. Navy, was a former test pilot. Selected by NASA in April 1996, McCool was making his first spaceflight.<br clear=left />\r\n</p>\r\n<p>\r\n<img align=left src="http://www.nasa.gov/images/content/columbia/crew/thumb_michaela.jpg" width="64" height="48" alt="Michael P. Anderson, Payload Commander" border="0" />\r\nMichael P. Anderson, Payload Commander</a><br />\r\n\r\n		Michael P. Anderson, 43, a lieutenant colonel in the U.S. Air Force, was a former instructor pilot and tactical officer. Anderson logged over 211 hours in space.<br clear=left />\r\n</p>\r\n<p>\r\n<img align=left src="http://www.nasa.gov/images/content/columbia/crew/thumb_davidb.jpg" width="64" height="48" alt="David M. Brown, Mission Specialist 1" border="0" />David M. Brown, Mission Specialist 1</a><br />\r\n		David M. Brown, 46, a captain in the U.S. Navy, was a naval aviator and flight surgeon. Selected by NASA in April 1996, Brown was making his first spaceflight.<br clear=left />\r\n</p>\r\n<p>\r\n<img align=left src="http://www.nasa.gov/images/content/columbia/crew/thumb_kalpanac.jpg" width="64" height="48" alt="Kalpana Chawla, Mission Specialist 2" border="0" />Kalpana Chawla, Mission Specialist 2</a><br />\r\n		Kalpana Chawla, 41, was an aerospace engineer and an FAA Certified Flight Instructor. Selected by NASA in December 1994, Chawla logged more than 376 hours in space.<br clear=left />\r\n</p>\r\n<p>\r\n<img align=left src="http://www.nasa.gov/images/content/columbia/crew/thumb_laurelc.jpg" width="64" height="48" alt="Laurel Blair Salton Clark, Mission Specialist 4" border="0" />Laurel Blair Salton Clark, Mission Specialist 4</a><br />\r\n		Laurel Clark, 41, was a commander (captain-select) in the U.S. Navy and a naval flight surgeon. Selected by NASA in April 1996, Clark was making her first spaceflight.<br clear=left />\r\n</p>\r\n<p>\r\n<img align=left src="http://www.nasa.gov/images/content/columbia/crew/thumb_ilanr.jpg" width="64" height="48" alt="Ilan Ramon, Payload Specialist1" border="0" />Ilan Ramon, Payload Specialist 1</a><br />\r\n		Ilan Ramon, 48, a colonel in the Israeli Air Force, was a fighter pilot who was the only payload specialist on STS-107. Approved by NASA in 1998, he was making his first spaceflight.<br clear=left />\r\n</p>\r\n\r\n','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(41,' Calaveras Big Trees State Park','2003-04-05','Tom & Denise Klisz','Start is at the El Dorado Hills Park N Ride, off Hwy at the corner of Latrobe Rd. and White Rock Rd. The first half is an interesting route through the Gold Rush country.  The second half is backroads less traveled.  It is about 2 hours to the park.  At the state park, take a guided hike and peek into the visitor''s center.  There is a $4 entry fee to the park and the guided hike is $2 per person.',replace(replace('0| Start El Dorado Hills Park N Ride\r\n.005| Turn right out of parking lot\r\n.01| Turn right at stop sign (White Rock Rd.)\r\n.02| Turn left onto Latrobe Rd. (stop light)\r\nWe will regroup along Latrobe Rd. near business park\r\n16.6| Turn left when Latrobe Rd. ends at Hwy 16 (stop sign)\r\n19.3| Merge right onto Hwy 49 (south)\r\nFirst & only potty stop in Sutter Creek.  Depending on number of cars, we most likely will turn left onto Church St.  Public restrooms are located behind City Hall.  For those of us who need another latte or pastry, about a 1/2 block walk to a coffee house and several good bakeries.\r\n\r\nIt is about another 1.25 hours to the state park from here.\r\n\r\nContinuing on Hwy 49, in Martell and Jackson the road becomes 2 lanes each way.  Suggest using both lanes attempting to keep us together going through the stop sign and stop lights.  Will regroup as necessary.  Stay alert!\r\n\r\nTurn left back onto Hwy 49 south, we will regroup up the hill about 0.2 miles\r\n37.1| Turn left onto Route 26 in Mokelumne Hill\r\n38.6| Turn right onto Jesus Maria Rd.\r\n51.4| Turn right onto Railroad Flat Rd.\r\n55.4| Turn left onto Sheep Ranch Rd.\r\n62.6| Turn left onto Avery Sheep Ranch Rd.\r\n67.2| Turn left onto Hwy 4\r\n74.1| Turn right into Calaveras Big Trees State Park\r\n\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Return Routes:\r\n<p>\r\n1.  Afraid of getting lost?  Stay on westbound Hwy 4 until Hwy 49 in Angels Camp, turning right (north), and heading back to Hwy 50.\r\n</p>\r\n<p>\r\n2. Feeling just a little adventurous?  Once on Hwy 4, when you get to Murphys (approximately 38 miles from the park), turn right on Main St. and continue about 5 miles.  Turn left onto Murphys Grade.  You will end up at Hwy 49 north of Angels Camp.  (It is actually a faster route with less traffic and great roads.)\r\n</p>\r\n<p>\r\n3.  Denise & tom Route: From westbound Hwy 4, turn right onto Sheep Ranch Rd. in Avery.  It is very well marked!  About 4 miles there is a fork in the road; but either way goes to Sheep Ranch Rd.\r\n</p>\r\n<p>\r\nA. If you went to the right, continue on Sheep Ranch Rd.<br>\r\nB. If you went to the left, you took Fullen Rd. which will end at Sheep Ranch Rd. after about 5 miles.  Turn right onto Sheep Ranch Rd.  Turn left onto Mountain Ranch Rd. which will lead you to Hwy 49 in San Andreas.  Turn right onto northbound Hwy 49. all the way to Hwy 50.\r\n','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(42,' Sierra Nevada Brewery Lunch & Tour','2003-05-31',' Joe & Carol Rainbolt','A fun run ending in Chico at the Sierra Nevada Brewery.  After lunch in the restaurant, join a tour of the brewery next door.  Start from the Kmart shopping center in Grass Valley (off Hwy 49 at McKnight Way).',replace(replace('00| Exit Kmart shopping center and turn right.  Get into the left lane and turn left at the 2nd light to get onto Hwy 49/20 east bound.\r\n\r\n2.6| Turn left onto Hwy 49 towards Downieville\r\n9.3| Continue on Hwy 49 over the Yuba River crossing\r\n16.3| Continue on Hwy 49 through North San Juan\r\n22.5| Turn left at the Dobbins turnoff onto Marysville Road\r\n28.8| Go past the Bullards Bar Reservoir dam\r\n41.6| At the "T" turn left onto Willow Glen Road\r\n44.1| Turn right onto Loma Rica Road\r\n45.5| Turn right onto Los Verjeles Road.  The road is a little bit rough here for about 3 miles.\r\n50.4| Go straight at the stop sign.  This is now Oroville Bangor Hwy.\r\n56.8| Turn left at stop sign onto Foothill Blvd.\r\n64.9| Turn left at the stop light onto Olive Hwy.  Go straight through 3 stop lights (street name now changes to Washington Ave.).  Go through one more stop light, one stop sign, then another stop light.\r\n66.7| Turn right into the parking lot for the Feather River Hatchery.  We will stop here for a potty break.\r\nExit the parking lot and turn right to loop around, then turn right onto Table Mountain Blvd.\r\n68.6| Turn left onto Garden Dr.\r\n68.9| Turn right onto scenic Hwy 70.\r\n72.7| Turn left to Chico at the Hwy 149 junction.  YIELD TO ONCOMING TRAFFIC.\r\n77.2| Turn right onto Hwy 99.\r\n86.7| Take the exit at 20th Street.  Turn left and go over the freeway.\r\n87.2| Turn left onto Nevada Brewery Court.  Parking is available in front or on the left, across from the brewery.','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(43,' Full Moon Run','2003-07-12','Rod Gonzalez & Cat Black','Start at the El Dorado Hills McDonalds for a short drive through the hills, ending in Cameron Park for dessert or a late dinner.  This is an evening drive designed for gazing at the full moon.',replace(replace('0| Start at the El Dorado Hills McDonalds, 4312 Town Center Blvd.\r\n0.1| Left out of McDonalds parking lot to Town Center Blvd. and left to Latrobe Rd.\r\n0.9| Right at the signal onto Latrobe Rd.  Continue on Latrobe Rd. under the freeway.  It will become El Dorado Hills Blvd.  Take the right lane turn off onto Serrano Parkway.  We will regroup on Serrano Pkwy. if necessary.\r\n2.0| Continue on Serrano Pkwy. to Silva Valley Pkwy.\r\n4.9| Left on Silva Valley Pkwy. to Green Valley Rd.  Right on Green Valley Rd.\r\n13.2| Take Green Valley Rd. to N. Shingle Rd.  Right on N. Shingle Rd.\r\n17.4| N. Shingle to Ponderosa Rd.  Left on Ponderosa Rd.  Cross over the freeway and take a left onto Mother Lode Dr. and then left into the Gold Harvest Market parking lot for a brief pit stop.\r\n25.7| Right out of parking lot back onto Mother Lode Dr. and then left onto S. Shingle Rd. to Latrobe Rd.\r\n26.3| Cross Latrobe Rd. Stay on S. Shingle Rd. to Miller Hill School parking lot (right turn).\r\nWe will now view the moon and city lights from this scenic hilltop spot.\r\n34.6| Left out of parking lot back onto S. Shingle Springs Rd. to Latrobe Rd.  Cross Latrobe Rd. and continue north on S. Shingle Rd. to Durock Rd.\r\n36.9| Left onto Durock Rd. to Cameron Park Dr.  Right on Coach Ln. (at the signal) and right into the Denny''s parking lot.\r\n','\r',char(13)),'\n',char(10)),'','','',NULL,X'',NULL,0.0,'',0,0);
INSERT INTO routes VALUES(44,' 2003 Hot August Nights Ice Cream Run','2003-08-16','Bob & Genny Paauwe','A short evening fun run through the foothills, ending at Tasty Time in Folsom.  The start is at the Cameron Park McDonald''s: Hwy. 50 at the Cameron Park Dr. exit, south of the freeway.',replace(replace('0.0| RIGHT out of parking lot onto Durock Rd.\r\n2.15| RIGHT at signal onto South Shingle Rd.\r\n10.3| LEFT at stop onto Latrobe Rd. (regroup after turn, if needed)\r\n16.6| STRAIGHT at stop (Old Sacramento Rd.)\r\n18.8| LEFT at stop onto Jackson Hwy.\r\n19.1| RIGHT on Carbondale Rd. (occurs quickly; regroup if needed)\r\n21.6| LEFT onto Irish Hill Rd.\r\n27.3| RIGHT at stop (T intersection) onto Twin Cities Rd. (Hwy 104)\r\n28.2| RIGHT at Y onto Ione Michigan Bar Rd.\r\n37.3| LEFT at stop onto Jackson Hwy.\r\nSTRAIGHT at next 2 signals\r\n40.9| RIGHT on Stone House Rd. (regoup if needed)\r\n(note: Stone House Rd. briefly becomes Latrobe Rd.)\r\n42.5| RIGHT on Scott Rd. -- watch for potholes!\r\n50.4| LEFT on White Rock Rd.\r\n60.0| RIGHT at stop onto Prairie City Rd.\r\nSTRAIGHT at next 3 signals\r\n53.6| LEFT at signal onto Iron Point Rd.\r\n53.7| RIGHT into Safeway shopping center (occurs quickly)\r\n\r\nEAT ICE CREAM!!!\r\n\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Tasty Time will be ready for our large group.  They should be able to quickly fill our orders so we can have time to socialize.\r\n\r\nTasty Time is located in the Safeway shopping center on the corner of Prairie City Rd. and Iron Point Rd.  They can be reached at (916) 351-0748.','\r',char(13)),'\n',char(10)),'','',NULL,X'',NULL,0.0,'',0,0);
