INSERT INTO routes VALUES(55,' Ice Cream Fun Run 2009       ','2009-07-25',' Bob & Genny Paauwe       ',replace(replace('Care to follow us down a rocky road?  This fun run is sherbet to be\r\nsatisfying!  Everyone likes a frozen treat on a hot night, so\r\njoin us for this evening event which will end at the Tasty Time ice cream parlor in Folsom. The run should be under 2 hours in length.','\r',char(13)),'\n',char(10)),replace(replace('0.00|Out of the parking lot and RIGHT onto Durock Rd.\r\nRIGHT onto South Shingle\r\n10.25|LEFT onto Latrobe Rd.\r\nLEFT onto Old Sacramento Rd.\r\nLEFT onto Hwy 49 (in Plymouth)\r\n29.40|RIGHT onto Union Mine Rd. (Watch for deer!)\r\nLEFT at T (stay on Union Mine Rd.)\r\nRIGHT onto Hwy 49\r\nRIGHT onto Pleasant Valley Rd. / Hwy 49\r\nLEFT onto Forni Rd. (comes up quickly)\r\nSTRAIGHT at Missouri Flat Rd. (Signal)\r\nRIGHT at T intersection at Placerville Dr. (staying on Forni Rd.)\r\nLEFT onto Ray Lawyer Dr.\r\n44.00|STRAIGHT at SIGNAL onto Green Valley Rd.\r\nRIGHT at T intersection, staying on Green Valley Rd.\r\nLEFT at T intersection, staying on Green Valley Rd.\r\nRIGHT at Green Valley Rd.\r\nSTRAIGHT at SIGNALS, get into left lane after Mormon Island.\r\n59.2|LEFT at SIGNAL onto Sophia Pkwy.\r\n63.0|RIGHT at T intersection onto Iron Point Rd.\r\nSTRAIGHT at SIGNALS (12 total)\r\n67.20|RIGHT into parking lot after crossing Prairie City Rd.\r\n-|Tasty Time faces Prairie City Rd., to the left of Safeway.  There should be plenty of parking.','\r',char(13)),'\n',char(10)),replace(replace('Eat ice cream at Tasty Time!\r\n<p>\r\nFinish is at Tasty Time<br>\r\n1870 Prairie City Rd.\r\nFolsom (corner of Prairie City Rd. and Iron Point Rd.)<br>','\r',char(13)),'\n',char(10)),replace(replace('<p>\r\nMeet at the Cameron Park McDonald’s (6:30 p.m.), Hwy. 50 at Cameron Park\r\nexit, south of the freeway.  \r\n<p>\r\nThe fun run will\r\nstart promptly at 6:45 p.m., where we’ll shake it up on the back roads\r\nof El Dorado County.  Don’t be (choco)late!','\r',char(13)),'\n',char(10)),'        ',NULL,X'',NULL,0.0,'',0,0);
