INSERT INTO routes VALUES(64,'Awful (Annie''s) Fun Run','2022-08-06','Bob & Genny Paauwe             ',replace('Join us for a foothill fun run ending with lunch at Awful Annie''s in Auburn. You won''t be disappointed with the number of twists and turns on this route! We''ll be driving in El Dorado and Placer counties where the roads were paved for Miatas.<br/>\n<br/>\nSTART:<br>\nEl Dorado Hills Town Center<br>\nCorner of Mercedes Lane and Vine Street, across from the Mercedes-Benz dealership at 1000 Mercedes Ln., El Dorado Hills\n','\n',char(10)),replace('0|Left onto Mercedes Ln.\n0.05|Left onto Vine St.\n0.4|Left at signal onto White Rock Rd.\n1.1|Over Hwy 50, road becomes Silva Valley Pkwy.\n5.1|Right at signal onto Green Valley Rd.\n-|Slow for construction zone\n13.4|Left at Tee (signal) staying on Green Valley Rd.\n14.0|Stay left onto Lotus Rd.\n19.8|Straight at Stop\n20.8|Left at stop onto Hwy 49 north\n-|Cross American River\n21.3|Right at stop onto Marshall Rd.\n21.9|Left onto Prospectors Rd.\n24.6|Left at stop onto Marshall Rd. (be careful, hard to see oncoming traffic)\n30.4|Right onto Lower Main St.\n30.5|Quick left at stop onto Hwy 193 (in Georgetown)\n42.7|Right at stop onto Hwy 49 (in Cool)\n-|Cross the American River (keep your feet dry)\n46.3|Left at stop staying on Hwy 49 north\n48.6|Right at signal onto Lincoln Way\n50.2|Straight at Foresthill Rd., staying on Lincoln Way\n50.4|Right into Awful Annie''s (see below for parking notes)\n-|END\n','\n',char(10)),replace('Park where you can find a spot; also parking at rear of building.  Don''t park in the hotel parking.<br/>\nIf no spots are available, go back the way we came to Silver Bend Way (will be on your left), just past Taqueria el Burrito and park on the street.<br/>\n\nAwful Annie''s, 13460 Lincoln Way, Auburn<br>\n<p>\nGenny''s mobile: (916) 801-4211\n','\n',char(10)),'','',NULL,X'',NULL,50.399999999999998577,'1:30',0,0);
